public boolean validate()
{
    boolean ret = true;
    int unlockRet;

    super();

    if(strFind(SKS_PP_Setup.RegUnlockKey,':', 0, strLen(SKS_PP_Setup.RegUnlockKey)))
    {
        container keyCon = str2con(SKS_PP_Setup.RegUnlockKey, ':');
        int formatCount = conPeek(keyCon,2);
        str modules = conPeek(keyCon,3);

        SKS_PP_Setup.RegPP = strFind(modules,'A', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegBR = strFind(modules,'B', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegLB = strFind(modules,'C', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegAPEFT = strFind(modules,'D', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegAREFT = strFind(modules,'E', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegFX = strFind(modules,'F', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegPaymFactoryAutomation = strFind(modules,'G', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegInhouseBank = strFind(modules,'H', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegMandateAutomation = strFind(modules,'I', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegMCFAPDD = strFind(modules,'J', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegCCReconciliationExtensions = strFind(modules,'K', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegSCF = strFind(modules,'L', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegBTB = strFind(modules,'M', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegCNP = strFind(modules,'N', 0, strLen(modules)) > 0;
        SKS_PP_Setup.RegPCard = strFind(modules,'O', 0, strLen(modules)) > 0; // 21186

        SKS_PP_Setup.EFTLicensedFormats = formatCount;
    }

    if(strlen(strrtrim(strltrim(SKS_PP_Setup.RegUnlockKey))) > 0)
    {
        unlockRet = element.validateUnlockKey();

        if(unlockRet == -1)
        {
            box::info("@SKS246","@SKS225");
            ret = false;
        }
        else if(unlockRet == -2)
        {
            sks_pp_setup.EFTLicensedFormats = 0;
            box::info("@SKS1443", "@SKS225");

            ret = false;
        }
        else if(unlockRet == 1)
        {
            if(Box::yesNo("@SKS249" + ' ' + "@SKS:SKGProdConfig", DialogButton::No) ==  DialogButton::Yes)

            {
                Args args = new Args();
                args.parmEnum(NoYes::No);
                SKS_InfoFile::main(args);
            }

            // When an unlocking key is successfully clear out the registration notification counters
            ttsbegin;
            SKS_EB_RegNotifyTracker lSKS_EB_RegNotifyTracker;
            delete_from lSKS_EB_RegNotifyTracker;
            ttscommit;

            if(!SKS_EB_RegNotify.EmailNotification && SKS_PP_Setup.RegUnlockKey)
            {
                info("@SKS:SKGRegNotifyEmailWarning");
            }

            ret = true;
        }
        else
        {
            ret = true;
        }
    }

    return ret;
}

