public int active()
{
    int ret;

    ret = super();

    if(SKS_LB_Setup_AutoCRParms.RevCashDiscSource == SKS_LB_ReversingSource::Customer)
    {
        SKS_LB_Setup_AutoCRParms_ds.object(fieldNum(SKS_LB_Setup_AutoCRParms, CustCashDiscCode)).allowedit(false);
    }
    else
    {
        SKS_LB_Setup_AutoCRParms_ds.object(fieldNum(SKS_LB_Setup_AutoCRParms, CustCashDiscCode)).allowedit(true);
    }


    if(SKS_LB_Setup_AutoCRParms.RevTOPSource == SKS_LB_ReversingSource::Customer)
    {
        SKS_LB_Setup_AutoCRParms_ds.object(fieldNum(SKS_LB_Setup_AutoCRParms, CustPaymTermCode)).allowedit(false);
    }
    else
    {
        SKS_LB_Setup_AutoCRParms_ds.object(fieldNum(SKS_LB_Setup_AutoCRParms, CustPaymTermCode)).allowedit(true);
    }

    return ret;
}

public boolean validate()
{
    SKS_LB_WriteOffDetails SKS_LB_WriteOffDetails;
    SKS_LB_JournalAR       SKS_LB_JournalAR;
    SKS_LB_JournalARErrors SKS_LB_Journalarerrors;

    changeCompany(SKS_LB_Setup_AutoCRParms.CustomerCompany ? SKS_LB_Setup_AutoCRParms.CustomerCompany : curext())
    {
        if(!SKS_LB_Setup_AutoCRParms.AutoCRGenJrnlName)
        {
            select firstOnly SKS_LB_WriteoffDetails where SKS_LB_WriteoffDetails.DataAreaId == SKS_LB_Setup_AutoCRParms.customercompany && SKS_LB_WriteoffDetails.autorev == NoYes::Yes;

            if(SKS_LB_WriteoffDetails)
            {
                error("@SKS2154");
                return false;
            }

            select firstOnly SKS_LB_JournalAR where SKS_LB_JournalAR.AutoCRRev == NoYes::Yes && SKS_LB_JournalAR.LedgerJournalTransRecId != 0;

            if(SKS_LB_JournalAR)
            {
                error("@SKS3506");
                return false;
            }

            select firstOnly SKS_LB_Journalarerrors where SKS_LB_Journalarerrors.AutoCRRev == NoYes::Yes;

            if(SKS_LB_Journalarerrors)
            {
                error("@SKS3506");
                return false;
            }

            return true;
        }
        else
        {
            if(!LedgerJournalName::exist(SKS_LB_Setup_AutoCRParms.AutoCRGenJrnlName))
            {
                return checkFailed(strFmt("@SKS1210", SKS_LB_Setup_AutoCRParms.AutoCRGenJrnlName, "@SYS35283" ,"@SYS1436"));
            }
            else
            {
                return true;
            }
        }
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    SysTableLookup sysTableLookup;
    changeCompany (SKS_LB_Setup_AutoCRParms.CustomerCompany ? SKS_LB_Setup_AutoCRParms.CustomerCompany : curext())
    {
        // Create an instance of SysTableLookup where 'this'
        // is the current form control.
        sysTableLookup =SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);

        // Add fields to be shown in the lookup form.
        sysTableLookup.addLookupField(fieldNum(LedgerJournalName, JournalName));
        sysTableLookup.addLookupField(fieldNum(LedgerJournalName, Name));

        // Limit and arrange the data selection.
        queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));
        queryBuildRange = queryBuildDataSource.addRange(
        fieldNum(LedgerJournalName, JournalType));
        queryBuildRange.value('0');
        sysTableLookup.parmQuery(query);

        // Perform the lookup.

        sysTableLookup.performFormLookup();
    }
}

