/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validate()
{
    boolean ret;
    SKS_EPY_Setup epySetupLoc;

    ret = super();            

    if(SKS_EPY_Setup.sendPDF == NoYes::No)
    {
        ttsbegin;
        select forupdate firstonly epySetupLoc;
        epySetupLoc.sendPDF = NoYes::No;

        epySetupLoc.UsePrintMgmtSalesInvoice = NoYes::No;
        epySetupLoc.UsePrintMgmtProjInvoice = NoYes::No;
        epySetupLoc.UsePrintMgmtFreeTextInvoice = NoYes::No;
        epySetupLoc.update();
        ttscommit;

        SKS_EPY_Setup_ds.reread();
        SKS_EPY_Setup_ds.refresh();
        SKS_EPY_Setup_ds.research(true);
    }

    return ret;
}

