public boolean validate()
{
    boolean ret;
    SKS_EPY_Setup locSKS_EPY_Setup;

    ret = super();

    if(ret && SKS_EPY_Setup.WebServLoc != '')
    {
        select firstOnly crossCompany WebServLoc, DataAreaId from locSKS_EPY_Setup where
            locSKS_EPY_Setup.WebServLoc == SKS_EPY_Setup.WebServLoc &&
            locSKS_EPY_Setup.dataAreaId != curext();

        if(locSKS_EPY_Setup)
        {
            error(strFmt("@SKS3442", locSKS_EPY_Setup.company()));
            ret = false;
        }
    }
    return ret;
}

