/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if(SKS_EB_RegNotify.EmailNotification)
        {
            if(!SKS_EB_RegNotify.EmailTo)
            {
                return checkFailed(strfmt("@SKS2468",
                    SKS_EB_RegNotify_ds.object(fieldNum(SKS_EB_RegNotify,EmailTo)).labelText()));
            }
            if(!SKS_EB_RegNotify.EmailFrom)
            {
                return checkFailed(strfmt("@SKS2468",
                    SKS_EB_RegNotify_ds.object(fieldNum(SKS_EB_RegNotify,EmailFrom)).labelText()));
            }
            if(!SKS_EB_RegNotify.EmailSubject)
            {
                return checkFailed(strfmt("@SKS2468",
                    SKS_EB_RegNotify_ds.object(fieldNum(SKS_EB_RegNotify,EmailSubject)).labelText()));
            }
        }
    }

    return ret;
}

