public void arEFTcheckbox_chk()
{
    DocuType   locDocuType;
    DocuActionArchive DocuActionArchive = new DocuActionArchive();
    if(SKS_EFT_Setup_ModAREFT.value())
    {
        SKS_EFT_Setup_ModAREmail.enabled(true);
        locDocuType = DocuType::find('Direct Debit');
        if(!locDocuType)
        {
            locDocuType.name = 'Direct Debit';
            locDocuType.TypeID = 'Direct Debit';
            locDocuType.TypeGroup          = DocuTypeGroup::File;
            locDocuType.DocuStructureType  = DocuStructureType::None;
            locDocuType.fileplace = DocuFilePlace::Archive;
            locDocuType.ActionClassID = classIdGet(DocuActionArchive);
            locDocuType.insert();
        }
        locDocuType = DocuType::find('DirectDebit-Unsigned');
        if(!locDocuType)
        {
            locDocuType.name = 'DirectDebit-Unsigned';
            locDocuType.TypeID = 'DirectDebit-Unsigned';
            locDocuType.TypeGroup          = DocuTypeGroup::File;
            locDocuType.DocuStructureType  = DocuStructureType::None;
            locDocuType.fileplace = DocuFilePlace::Archive;
            locDocuType.ActionClassID = classIdGet(DocuActionArchive);
            locDocuType.insert();
        }
        locDocuType = DocuType::find('DirectDebit-Signed');
        if(!locDocuType)
        {
            locDocuType.name = 'DirectDebit-Signed';
            locDocuType.TypeID = 'DirectDebit-Signed';
            locDocuType.TypeGroup          = DocuTypeGroup::File;
            locDocuType.DocuStructureType  = DocuStructureType::None;
            locDocuType.fileplace = DocuFilePlace::Archive;
            locDocuType.ActionClassID = classIdGet(DocuActionArchive);
            locDocuType.insert();
        }
    }
    else
    {
        SKS_EFT_Setup_ModAREmail.enabled(false);
        SKS_EFT_Setup.ARCreateCustBankrecord = NoYes::No;
    }
}

public void clicked()
{
    super();

    this.arEFTcheckbox_chk();
    element.disableAREFT();
}

