public void archivecheckbox_chk()
{

    //if archive checkbox is clicked enable archive path, otherwise disable the path
    if( SKS_PP_Setup_PPArchive.value() && SKS_PP_Setup.ModPosPay == NoYes::Yes)
    {
        SKS_PP_Setup_PPPathArchive.enabled(true);
    }
    else
    {
        SKS_PP_Setup_PPPathArchive.enabled(false);
    }
}

