/// BR 5 enabling notify statements if both create statements and create statements if no transactions marked
/// </summary>
/// <returns>true or false</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if(!SKS_PP_Setup.BRCreateBankStatements || !SKS_PP_Setup.BRCreateBankStatementsIfNoTrans)
    {
        SKS_PP_Setup.BRCreateBankStatementsIfNoTrans = NoYes::No;
    }
    element.setBRControlProperties();

    return ret;
}

public boolean validate()
{
    boolean ret;
    ret = super();
    if(ret && this.checked() && !SKS_PP_Setup.BRAutomaticallyPostGL)
        info("@SKS2928");

    return ret;
}

