/// <summary>
/// This method is used to pass the Batch Job caption to the "BatchJob" form to show only selected Bactch Job in the form control.
/// </summary>
public void jumpRef()
{
    //<ISSUE-14057 - Open "Batch Job" Form, and not "Batch Tasks" Form when click View Details>
    Args args = new Args();
    FormRun formRun;

    if(SKS_PP_Setup_BRBatchJobId.value())
    {
        Batch::jumpRef(SKS_PP_Setup_BRBatchJobId.value());
    }
    else
    {
        args.name(formStr(BatchJob));
        formRun = classFactory.formRunClass(args);
        formRun.init();
        formRun.run();
    }
    //</ISSUE-14057>
}

