void archivecheckbox_chk()
{
    //if archive checkbox is clicked enable archive path, otherwise disable the path
    if(SKS_PP_Setup.LBArchive == NoYes::Yes && SKS_PP_Setup.ModLockbox == NoYes::Yes)
    {
        SKS_PP_Setup_LBPathArchive.enabled(true);
    }
    else
    {
        SKS_PP_Setup_LBPathArchive.enabled(false);
    }
}

public void clicked()
{
    super();

    this.archivecheckbox_chk();
}

