public boolean modified()
{
    boolean ret;

    ret = super();

    if(SKS_LB_Setup_AutoCRParms.RevTOPSource == SKS_LB_ReversingSource::Customer)
    {
        SKS_LB_Setup_AutoCRParms.CustPaymTermCode = '';
        SKS_LB_Setup_AutoCRParms_ds.object(fieldNum(SKS_LB_Setup_AutoCRParms, CustPaymTermCode)).allowedit(false);
    }
    else
    {
        SKS_LB_Setup_AutoCRParms_ds.object(fieldNum(SKS_LB_Setup_AutoCRParms, CustPaymTermCode)).allowedit(true);
    }

    return ret;
}

