public boolean modified()
{
    boolean ret;

    ret = super();

    if(SKS_PP_Setup.LBAutoPmtJrnl == NoYes::Yes)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, moveGoodPaymToBatch)).allowedit(true);
        SKS_PP_Setup_ds.object(fieldnum(SKS_PP_Setup, LBAutoCreateDepSlip)).allowEdit(true);
    }
    else
    {
        SKS_PP_Setup.MoveGoodPaymToBatch = NoYes::No;
        SKS_PP_Setup.NoPartialsettle = NoYes::No;
        SKS_PP_Setup.PaymMustBeZero = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, moveGoodPaymToBatch)).allowedit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, nopartialsettle)).allowedit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, paymmustbezero)).allowedit(false);

        if(!SKS_PP_Setup.LBPmtPost)
        {
            SKS_PP_Setup.LBAutoCreateDepSlip = NoYes::No;
            SKS_PP_Setup_ds.object(fieldnum(SKS_PP_Setup, LBAutoCreateDepSlip)).allowEdit(false);
        }
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if(SKS_PP_Setup.MoveGoodPaymToBatch == NoYes::No)
    {
        SKS_PP_Setup.NoPartialsettle = NoYes::No;
        SKS_PP_Setup.PaymMustBeZero = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, nopartialsettle)).allowedit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, paymmustbezero)).allowedit(false);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, nopartialsettle)).allowedit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, paymmustbezero)).allowedit(true);
    }

    return ret;
}

