{
    RecId batchJobRecid = SKS_LB_AutoSettlementBatch::createAutoSettlementBatch(element);

    if (batchJobRecid)
    {
        SKS_LB_AutoSettlementSetup.BatchJobId = batchJobRecid;
        SKS_LB_AutoSettlementSetup_ds.refresh();
        SKS_LB_AutoSettlementSetup_ds.executeQuery();
    }
}

