/// <summary>
/// Lookup batch references for dropdown
/// </summary>
/// <returns>relevant batch job</returns>
public Common lookupReference()
{
    Batch BatchJobID;
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    // Create an instance of SysTableLookup where 'this' is the current form control
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(Batch), this, true);
    query.allowCrossCompany(true);
    // Add fields to be shown in the lookup form
    sysTableLookup.addLookupField(fieldNum(Batch, Caption));
    // Limit and arrange the data selection
    queryBuildDataSource = query.addDataSource(tableNum(Batch));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(Batch, classnumber));
    queryBuildRange.value(int2str(classNum(SKS_LB_AutoSettlement)));
    sysTableLookup.parmQuery(query);
    // Perform the lookup
    BatchJobID = sysTableLookup.performFormLookup();

    return BatchJobID;
}

void clicked()
{
    Dialog              dialog;
    DialogField         dialogField;
    BankAccountTable    tmpBankAccountTable;
    CompanyInfo         tmpCompanyInfo;
    ;

    // Create Default Bank Account Information dialog
    dialog = new Dialog("@SKS1563");
    dialogField = dialog.addField(extendedTypeStr(CompanyBankAccountId));

    if (dialog.run())
    {
        // Populate Bank Account and Company Info table buffers
        select RegistrationNum, BankDestinationName, CompanyPaymId from  tmpBankAccountTable where tmpBankAccountTable.AccountID == dialogField.value();
        select name from tmpCompanyInfo where tmpCompanyInfo.dataAreaId == tmpBankAccountTable.dataAreaId;

        // Populate fields on the Electronic Funds Defaults tab
        SKS_EFT_Setup.ACHImmDest = tmpBankAccountTable.RegistrationNum;
        SKS_EFT_Setup.ACHImmDestName = tmpBankAccountTable.BankDestinationName;
        SKS_EFT_Setup.ACHImmOrig = tmpBankAccountTable.CompanyPaymId;
        SKS_EFT_Setup.ACHImmOrigName = tmpBankAccountTable.Name;
        SKS_EFT_Setup.ACHCompanyID = tmpBankAccountTable.CompanyPaymId;
        SKS_EFT_Setup.ACHCompanyName = tmpCompanyInfo.Name;
        SKS_EFT_Setup.ACHOrigDFI = tmpBankAccountTable.RegistrationNum;
        SKS_EFT_Setup.ACHOrigDFIName = tmpBankAccountTable.BankDestinationName;
    }
    else
    {
        // If they just cancelled the dialog, then no error.
        return;
    }
}

