    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this' is the current form control
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(Batch), CreditCardPLBatchInfo_CCPLBatchJobId, true);
    ;
    query.allowCrossCompany(true);

    // Add fields to be shown in the lookup form
    sysTableLookup.addLookupField(fieldNum(Batch, Caption));

    // Limit and arrange the data selection
    queryBuildDataSource = query.addDataSource(tableNum(Batch));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(Batch, classnumber));
    queryBuildRange.value(int2str(classNum(SKS_CC_PayLink)));


    sysTableLookup.parmQuery(query);

    // Perform the lookup
    BatchJobID = sysTableLookup.performFormLookup();

    return BatchJobID;
}

/// <summary>
/// 17420
/// Allow default CC from Projects will be enabled if 
/// Allow CC processing from invoice proposal field is enabled
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        // <17420>
        if(SKS_PP_Setup.CCAllowCCProcFromProjInvProposal == NoYes::Yes)
        {
            CreditCardProjSvcOrder_CCAllowDefaultCCFromProjects.enabled(true);
        }
        else
        {
            SKS_PP_Setup.CCAllowDefaultCCFromProjects = NoYes::No;
            CreditCardProjSvcOrder_CCAllowDefaultCCFromProjects.enabled(false);                    
        }
        // </17420>

    }

    return ret;
}

void clicked()
{
    boolean result;

    super();

    if(SKS_EPY_Setup.WebServLoc == '' || SKS_EPY_Setup.ClientId == '' || SKS_EPY_Setup.Secret == '')
    {
        error(strFmt(("@SKS3440"), "@SKS3274", "@SYS69163", "@SKS3378"));
    }
    else
    {
        result = SKS_EPY_Sync::TestConnection(SKS_EPY_Setup.WebServLoc, SKS_EPY_Setup.ClientId, SKS_EPY_Setup.Secret);
        if(result)
        {
            SKS_EPY_Setup.Activated = SKS_EPY_Activated::Activated;
            element.populateepayCurrency();
        }
        else
        {
            SKS_EPY_Setup.Activated = SKS_EPY_Activated::NotActivated;
            element.DeleteEpayCurrencyTable();
        }
        SKS_EPY_Setup_ds.refresh();
    }
}

void clicked()
{
    Query locQuery;
    QueryBuildDataSource locQueryBuildDataSource;
    SysQueryRun locQueryRun;
    CustTable locCustTable;
    SKS_EPY_CustFilter  IAEpayfilter;
    ;

    if(SKS_EPY_Setup.ePayQuery)
    {

        locQueryRun = new SysQueryRun(SKS_EPY_Setup.ePayQuery);
        locQuery = locQueryRun.query();
        locQueryBuildDataSource = locQuery.dataSourceTable(tableNum(CustTable));
    }
    else
    {
        locQuery = new Query();
        locQueryBuildDataSource = locQuery.addDataSource(tableNum(CustTable));
        locQueryRun = new SysQueryRun(locQuery);
    }
    locQueryRun.promptAllowAddDataSource(true);
    locQueryRun.promptAllowSave(false);
    locQueryRun.promptAllowAddRange(QueryAllowAdd::AllFields);
    locQueryRun.promptLoadLastUsedQuery(false);
    locQueryRun.form(formstr(SysQueryForm));

    locQueryRun.title("@SKS3396");
    if(locQueryRun.prompt())
    {
        SKS_EPY_Setup.ePayQuery = locQueryRun.pack();

        // SKS_EPY_CustFilter must be equal to "No" or "NotSet"
        locQueryBuildDataSource.addRange(fieldNum(CustTable, SKS_EPY_CustFilter))
            .value(queryValue(SKS_EPY_CustFilter::NotSet));

        locQueryBuildDataSource.addRange(fieldNum(CustTable, SKS_EPY_CustFilter))
            .value(queryValue(SKS_EPY_CustFilter::No));

        if(Box::yesNo("@SKS3395", DialogButton::No, "@SKS3396" ) == DialogButton::Yes)
        {
            ttsBegin;
            while(locQueryRun.next())
            {
                locCustTable = locQueryRun.get(tableNum(CustTable));
                locCustTable.selectForUpdate(true);
                locCustTable.SKS_EPY_CustFilter = SKS_EPY_CustFilter::YesByFilter;
                locCustTable.SKS_EPY_PaymentType = SKS_EPY_Setup::find().PaymentType;
                if(locCustTable.AccountNum != locCustTable.InvoiceAccount && locCustTable.InvoiceAccount != '')
                {
                    IAEpayfilter = CustTable::find(locCustTable.InvoiceAccount).SKS_EPY_CustFilter;
                    if(IAEpayfilter != SKS_EPY_CustFilter::Yes && IAEpayfilter != SKS_EPY_CustFilter::YesByFilter)
                    {
                        locCustTable.SKS_EPY_CustFilter = SKS_EPY_CustFilter::No;
                        Warning(strFmt("@SKS:SKGePayInvAcctValidation", locCustTable.AccountNum, locCustTable.InvoiceAccount));
                    }
                }
                locCustTable.update();
            }
            ttsCommit;
        }
    }
}

