void sendpdfcheckbox_chk()
{
    //if archive checkbox is clicked enable archive path, otherwise disable the path
    if( SKS_EPY_Setup_sendPDF.value() && SKS_PP_Setup.ModEPAY == NoYes::Yes)
    {
        if(SKS_EPY_Setup.ProjInv)
        {
            SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ProjInvReportController)).allowEdit(true);
            SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ProjInvReportName)).allowEdit(true);
        }
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ePayPDFFilePath)).allowEdit(true);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ePayInvFolderPath)).allowEdit(true);

        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, UsePrintMgmtSalesInvoice)).allowEdit(true);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, UsePrintMgmtProjInvoice)).allowEdit(true);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, UsePrintMgmtFreeTextInvoice)).allowEdit(true);
    }
    else
    {
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ProjInvReportController)).allowEdit(false);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ProjInvReportName)).allowEdit(false);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ePayPDFFilePath)).allowEdit(false);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, ePayInvFolderPath)).allowEdit(false);

        SKS_EPY_Setup_UsePrintMgmtSalesInvoice.value(0);
        SKS_EPY_Setup_UsePrintMgmtProjInvoice.value(0);
        SKS_EPY_Setup_UsePrintMgmtFreeTextInvoice.value(0);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, UsePrintMgmtSalesInvoice)).allowEdit(false);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, UsePrintMgmtProjInvoice)).allowEdit(false);
        SKS_EPY_setup_ds.object(fieldNum(SKS_EPY_Setup, UsePrintMgmtFreeTextInvoice)).allowEdit(false);
    }
}

public void clicked()
{
    SKS.ePayData.SKS_EPY_Return_In  SKS_EPY_Return_In;
    SKS.ePayData.Setting            lSetting;
    str                             errortext;
    lSetting = new SKS.ePayData.Setting();
    super();

    if(SKS_EPY_Setup.CustAutoReg)
    {
        lSetting.set_Name('AutoRegisterCustomer');
        lSetting.set_Value(true);
        if(strLen(SKS_EPY_Setup.WebServLoc) > 0)
        {
            sks_epy_return_in = SKS.ePay.ePayTokenCall::ePay_POST_Object(lSetting,
                                SKS_PP_StringFormats::strAssembleURL(SKS_EPY_Setup.WebServLoc,'setting'),
                                SKS_EPY_Setup.ClientId, SKS_EPY_Setup.Secret,
                                'POSTBRACKET', SKS_PP_StringFormats::strAssembleURL(SKS_EPY_Setup.WebServLoc ,'token'), false);

            if(SKS_EPY_Return_In.get_ErrorDesc() != null)
            {
                errortext = SKS_EPY_Return_In.get_ErrorDesc();
                error(errortext);
            }
        }

        SKS_EPY_Setup_DefaultCustomerSecret.setFocus();
        info("@SKS:SKGSetDefaultCustomerPassword");
    }
}

