    super();

    if(SKS_PL_Setup.PLWebServLoc == '' || SKS_PL_Setup.DeviceID == '' || SKS_PL_Setup.DevicePassword == '')
    {
        error(strFmt(("@SKS3440"), "@SKS3957", "@SYS69163", "@SKS3378"));
    }
    else
    {
        result = SKS_PL_Sync::TestConnection(SKS_PL_Setup.PLWebServLoc, SKS_PL_Setup.DeviceID, SKS_PL_Setup.DevicePassword);
        if(result)
        {
            SKS_PL_Setup.Activated = SKS_EPY_Activated::Activated;
        }
        else
        {
            SKS_PL_Setup.Activated = SKS_EPY_Activated::NotActivated;
        }
        SKS_EPY_Setup_ds.refresh();
    }
}

