public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource qbds;
    QueryBuildRange queryBuildRange;
    SysTableLookup sysTableLookup;

    sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),this);
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable, Name));
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));
    qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));
    queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable, Name));
    sysTableLookup.parmTmpBuffer(SKS_BF_TmpLookupTable::construct(SKS_BF_FileLocCategory::PCard, SKS_BF_LookupTable::FileLocations, SKG_BF.Models.Direction::In));
    sysTableLookup.performFormLookup();
}

public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource qbds;
    QueryBuildRange queryBuildRange;
    SysTableLookup sysTableLookup;

    sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),this);
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable, Name));
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));
    qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));
    queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable, Name));
    sysTableLookup.parmTmpBuffer(SKS_BF_TmpLookupTable::construct(SKS_BF_FileLocCategory::PCard, SKS_BF_LookupTable::PCardReconFormats, SKG_BF.Models.Direction::In));
    sysTableLookup.performFormLookup();
}

public void lookup()
{
    FormControl formControl = SKS_PCardParmTable_LedgerDimension;

    switch(SKS_PCard_ParmTable.AccountTypeActual)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(formControl,
                                                curExt());
            break;

        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(formControl,
                                        curExt());
            break;

        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(formControl,
                                            curExt());
            break;

        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(formControl,
                                    SKS_PCard_ParmTable);
            break;

        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(formControl,
                                    curExt());
            break;

        default:
            super();
            break;
    }
}

