}

private void updateKeysInAllLegalEntities()
{
    SKS_PP_Setup locSKS_PP_Setup;
    ttsbegin;//Note: this is ALL or NOTHING:  if something happens, no company will recieve the new key & settings
    while select forupdate crosscompany locSKS_PP_Setup where locSKS_PP_Setup.RecId != SKS_PP_Setup.Recid
    {
        changecompany(locSKS_PP_Setup.company())
        {
            locSKS_PP_Setup.RegPP = SKS_PP_Setup.RegPP;
            locSKS_PP_Setup.RegBR = SKS_PP_Setup.RegBR;
            // 21186
            locSKS_PP_Setup.RegPCard = SKS_PP_Setup.RegPCard;
            // 21186
            locSKS_PP_Setup.RegLB = SKS_PP_Setup.RegLB;
            locSKS_PP_Setup.RegAPEFT = SKS_PP_Setup.RegAPEFT;
            locSKS_PP_Setup.RegPaymFactoryAutomation = SKS_PP_Setup.RegPaymFactoryAutomation;
            locSKS_PP_Setup.RegInhouseBank = SKS_PP_Setup.RegInhouseBank;
            locSKS_PP_Setup.RegFX = SKS_PP_Setup.RegFX;
            locSKS_PP_Setup.RegAREFT = SKS_PP_Setup.RegAREFT;
            locSKS_PP_Setup.RegMandateAutomation = SKS_PP_Setup.RegMandateAutomation;
            locSKS_PP_Setup.EFTLicensedFormats = SKS_PP_Setup.EFTLicensedFormats;
            locSKS_PP_Setup.RegUnlockKey = SKS_PP_Setup.RegUnlockKey;
            locSKS_PP_Setup.RegMCFAPDD = SKS_PP_Setup.RegMCFAPDD;
            locSKS_PP_Setup.RegCCReconciliationExtensions = SKS_PP_Setup.RegCCReconciliationExtensions;
            locSKS_PP_Setup.RegSCF = SKS_PP_Setup.RegSCF;
            locSKS_PP_Setup.RegBTB = SKS_PP_Setup.RegBTB;
            locSKS_PP_Setup.RegCNP = SKS_PP_Setup.RegCNP;
            locSKS_PP_Setup.update();
        }
    }
    ttscommit;
}

/// <summary>
///
/// </summary>
public void clicked()
{
    int unlockRet;

    super();
    if(strlen(strrtrim(strltrim(SKS_PP_Setup.RegUnlockKey))) > 0)
    {
        unlockRet = element.validateUnlockKey();
        if(unlockRet == -1)
        {
            error(strFmt("@SKS:SKGOnlyValidUnlockKeysCanBeUsed", "@SKS246"),"@SKS225");
        }
        else if(unlockRet == -2)
        {
            sks_pp_setup.EFTLicensedFormats = 0;
            error(strFmt("@SKS:SKGOnlyValidUnlockKeysCanBeUsed","@SKS1443"), "@SKS225");
        }
        else if(unlockRet == 1)
        {
            if(Box::okCancel(strFmt("@SKS:SKGConfirmUpdateLicenseInfo",
                "@SKS311"),DialogButton::Cancel, "@SKS225") == DialogButton::Ok)
            {
                this.updateKeysInAllLegalEntities();
                info("@SKS:SKGAllUnlockingKeysUpdated","@SKS225");
            }
        }
    }
    else
    {
        error("@SKS:SKGValidUnlockKeyRequiredToUpdate");
    }
}

