/// <summary>
///
/// </summary>
public void jumpRef()
{
    str link;
    //str modules;
    System.Text.StringBuilder subject = new System.Text.StringBuilder();

    super();

    subject.Append("@SKS3");
    subject.Append(': ');
    subject.Append(CustomerName.text());
    subject.Append(' (');
    subject.Append(SKS_PP_Setup_RegCustomerID.text());
    subject.Append('), ');
    subject.Append("@SKS4");
    subject.Append(': ');
    subject.Append(SKS_PP_Setup_RegAuthorizedUsers.valueStr());
    subject.Append(', v');
    subject.Append(SKS_PP_Setup_RegVersion.text());
    subject.Append(', ');
    subject.Append("@SYS60337");
    subject.Append(': ');

    if ( SKS_PP_Setup.RegPP == NoYes::Yes)
    {
        subject.Append( 'PP ');
    }
    if ( SKS_PP_Setup.RegBR == NoYes::Yes)
    {
        subject.Append('ABR ');
    }
    if ( SKS_PP_Setup.RegLB == NoYes::Yes)
    {
        subject.Append('LB ');
    }
    if ( SKS_PP_Setup.RegAPEFT == NoYes::Yes)
    {
        subject.Append('APEFT ');
    }
    if ( SKS_PP_Setup.RegAREFT == NoYes::Yes)
    {
        subject.Append('AREFT ');
    }
    if ( SKS_PP_Setup.RegFX == NoYes::Yes)
    {
        subject.Append('FX ');
    }
    if ( SKS_PP_Setup.RegMCFAPDD == NoYes::Yes)
    {
        subject.Append('MCFAPDD ');
    }

    if (SKS_PP_Setup.RegAPEFT == NoYes::Yes ||
        SKS_PP_Setup.RegAREFT == NoYes::Yes)
    {
        subject.Append(num2str(SKS_PP_Setup.EFTLicensedFormats,0,0,0,0));
    }

    link = strfmt('mailto:%1?subject=%2', "@SKS5", subject.ToString());

    Browser b = new Browser();
    b.navigate(link, false, false);
}
