void abandonChanges()
{
    SKS_SCF_File    tmpSKS_SCF_File;
    SKS_SCF_FileNbr SKS_SCF_FileNbr;

    SKS_SCF_FileNbr = element.editSelectFileNumber( false, '');

    ttsbegin;
    while  select forupdate tmpSKS_SCF_File where tmpSKS_SCF_File.FileNbr == SKS_SCF_FileNbr
    {
        tmpSKS_SCF_File.Selected = false;
        tmpSKS_SCF_File.update();
    }
    ttscommit;
}

void clearSelected()
{
    element.editSelectFileNumber( true, '');
    nextactiondisplay.selectText(enum2str(SKS_PP_NextActionDisplay::Select));
    nextactiondisplay.enabled(false);
    BeginProcessing.enabled(false);

}

void ClearInvoices(SKS_SCF_FileNbr _fileNbr)
{
    VendTransOpen locVendTransOpen;

    ttsbegin;
    while select forupdate locVendTransOpen
                    where locVendTransOpen.SKS_SCF_FileNbr == _fileNbr
    {
        if(locVendTransOpen.RecId)
        {
            locVendTransOpen.SKS_SCF_Selected = false;
            locVendTransOpen.SKS_SCF_FileNbr = '';
            locVendTransOpen.SKS_SCF_File_RecId = 0;
            locVendTransOpen.update();
        }
    }
    ttscommit;
}

public void close()
{
    super();
    element.abandonChanges();
}

//BP Deviation Documented
edit SKS_SCF_FileNbr editSelectFileNumber( boolean set, SKS_SCF_FileNbr value )
{
    if (set)
    {
        SelectFileNumber = value;
    }

    return SelectFileNumber;
}

void EnableControlsFromAction()
{

    str nextBeginPrcng;    

    nextBeginPrcng = nextactiondisplay.getText(nextactiondisplay.selection());
    if ( nextBeginPrcng != Enum2str(SKS_PP_NextActionDisplay::Select))
    {
        BeginProcessing.enabled(true);
    }
    else
    {
        BeginProcessing.enabled(false);
    }
}

private void enableFileButtons()
{
    boolean fileExists;
    str errfilePathName;
    str logfilePathName;
    SKS_EB_FileHub fileHub = SKS_EB_FileHub::construct();

    // Error file
    errfilePathName = DSSKS_SCF_File.FilePath + '\\LOG\\' + DSSKS_SCF_File.FileName + '.err';

    fileHub.runFileInquire(errfilePathName);
    fileExists = fileHub.attrFileExists();

    if(fileExists)
    {
        errorfile.enabled(true);
    }
    else
    {
        errorfile.enabled(false);
    }

    // Log file
    logfilePathName = DSSKS_SCF_File.FilePath + '\\LOG\\' + DSSKS_SCF_File.FileName + '.log';

    fileHub.runFileInquire(logfilePathName);
    fileExists = fileHub.attrFileExists();

    if(fileExists)
    {
        logfile.enabled(true);
    }
    else
    {
        logfile.enabled(false);
    }

}

public void init()
{
    SKS_PP_Sys   sks_PP_Sys;

    super();

    lastNextActionDisplay = '';
    sks_PP_Sys = new SKS_PP_Sys();

    if (!sks_PP_Sys.ValidateSetup(this.name()))
    {
        this.close();
    }
    else
    {
        BeginProcessing.enabled(false);
    }
}

void LineStatusUpdates()
{
    if(DSSKS_SCF_File.KeepDelete == SKS_SCF_FileStatus::NotProcessed)
    {
        SelectViewInvoices.menuItemName('SKS_SCF_SelectViewFiles');
    }

    if(DSSKS_SCF_File.KeepDelete == SKS_SCF_FileStatus::Completed)
    {
        DSSKS_SCF_File_ds.allowDelete(false);
    }
    else
    {
        DSSKS_SCF_File_ds.allowDelete(true);
    }
    if(DSSKS_SCF_File.FileStatus == SKS_SCF_FileStatus::NotProcessed)
    {
        FileDetails.enabled(false);
    }
    else
    {
        FileDetails.enabled(true);
    }
    if(DSSKS_SCF_File.FileStatus == SKS_SCF_FileStatus::Completed)
    {
        SelectViewInvoices.enabled(false);
    }
    else
    {
        SelectViewInvoices.enabled(true);
    }
    this.enableFileButtons();
}

