}

public boolean validateDelete()
{
    boolean ret = true;
    SKS_SCF_File tmpSKS_SCF_File;
    SKS_SCF_FileDetail tmpSKS_SCF_FileDetail;

    if ( DSSKS_SCF_File.FileStatus == SKS_SCF_FileStatus::NotProcessed)
    {
        if(DSSKS_SCF_File.InvoiceCount == 0)
        {
            ret = true;
            return ret;
        }
        // Warning!  This file was created, but not completed.  If you delete the checks in the file will be available
        // to be reselected into another SCF file.  Are you sure you want to continue and delete this file?
        if( Box::okCancel( "@SKS:SCFFileDeleteMsg", DialogButton::No, "@SKS:SCFCreateSendFiles") == DialogButton::Cancel)
        {
            ret = false;
        }
        else
        {
            ttsbegin;

            delete_from tmpSKS_SCF_File
                where tmpSKS_SCF_File.FileNbr == DSSKS_SCF_File.FileNbr;

            delete_from tmpSKS_SCF_FileDetail
                where tmpSKS_SCF_FileDetail.FileNbr == DSSKS_SCF_File.FileNbr;

            ttscommit;
        }
    }
    else if ( DSSKS_SCF_File.FileStatus == SKS_SCF_FileStatus::Completed)
    {
        if( Box::okCancel( "@SKS:SCFFileCompleteDeleteMsg", DialogButton::No, "@SKS:SCFCreateSendFiles") == DialogButton::Cancel)
        {
            ret = false;
        }
        else
        {
            ttsbegin;

            delete_from tmpSKS_SCF_File
                where tmpSKS_SCF_File.FileNbr == DSSKS_SCF_File.FileNbr;

            delete_from tmpSKS_SCF_FileDetail
                where tmpSKS_SCF_FileDetail.FileNbr == DSSKS_SCF_File.FileNbr;

            ttscommit;
        }
    }
    if(ret == true && DSSKS_SCF_File.Selected == True)
    {
        element.editSelectFileNumber( True, '');
    }
    if ( ret == true && DSSKS_SCF_File.RecId > 0 )
    {
        element.ClearInvoices(DSSKS_SCF_File.FileNbr);
    }

    return ret;
}

public void modified()
{
    super();

