}

public boolean validate()
{
    boolean ret;

    ret = super();


    if ( DSSKS_SCF_File.FileNbr != element.editSelectFileNumber( false, '') )
    {

        if ( strlen( element.editSelectFileNumber( false, '')) > 0)
        {
            // --------------------------------------------------
            // Only one File can be selected at a time
            // --------------------------------------------------
            Box::warning("@SKS:SCFFileValidation","@SKS:SCFCreateSendFiles"); // MSG_ONLY_ONE_FILE_SELECTED_AT_A_TIME:24214
            return false;
        }
        else
        {
            // --------------------------------------------------
            // Can only select if some batches have been attached
            // --------------------------------------------------
            // Use BatchCount here as Void batches can have a zero Total Value
            if ( DSSKS_SCF_File.InvoiceCount == 0)
            {
                Box::warning("@SKS:SCFSelectedRecValidation","@SKS:SCFCreateSendFiles"); // MSG_CANNOT_SELECT_NO_BATCHES_SELECTED:24208
                return false;
            }
            // -------------------------------------------------------
            // If not yet processed, then test filename/path
            // -------------------------------------------------------
            else if ( DSSKS_SCF_File.KeepDelete != SKS_SCF_FileStatus::FileCreated  &&
                      DSSKS_SCF_File.KeepDelete != SKS_SCF_FileStatus::Completed &&
                      DSSKS_SCF_File.KeepDelete != SKS_SCF_FileStatus::Kept)
            {

                if ( element.TestFile( DSSKS_SCF_File.FilePath, DSSKS_SCF_File.FileName) == false)
                {
                    // Message already displayed in TesFile().
                    return false;
                }
            }

        }
    }

    return true;
}

void clicked()
{

    SKS_EB_CommProcess locSKS_EB_CommProcess;
    SKS_SCF_Processing sks_scf_Processing;
    SKS_PP_NextActionDisplay action;

    sks_scf_Processing = new SKS_SCF_Processing();

    str strNextActionDisplay = nextactiondisplay.getText(nextactiondisplay.selection());

    switch (strNextActionDisplay)
    {
        case Enum2str(SKS_PP_NextActionDisplay::CompleteKeep):
            action = SKS_PP_NextActionDisplay::CompleteKeep;
            break;
        case Enum2str(SKS_PP_NextActionDisplay::CreateSendFile):
            action = SKS_PP_NextActionDisplay::CreateSendFile;
            break;
        case Enum2str(SKS_PP_NextActionDisplay::Send):
            action = SKS_PP_NextActionDisplay::Send;
            break;
        case Enum2str(SKS_PP_NextActionDisplay::RetryComm):
            action = SKS_PP_NextActionDisplay::RetryComm;
            break;
    }

    str filebb = DSSKS_SCF_File.filepath;
    if(!SKS_EB_FileHub::directoryExistsForm(DSSKS_SCF_File.filepath))
    {
        return;
    }
    gSCFfileNbr = element.editSelectFileNumber(false,'');
    select locSKS_EB_CommProcess where locSKS_EB_CommProcess.CommID == DSSKS_SCF_File.CommID;
    sks_scf_Processing.AProcess_SCF(gSCFfileNbr, action, locSKS_EB_CommProcess, false);
    super();


    DSSKS_SCF_File_ds.research(true);

    DSSKS_SCF_File.Selected = false;

    element.LineStatusUpdates();
    element.clearSelected();
    DSSKS_SCF_File.update();
    DSSKS_SCF_File_ds.research(true);

}

