public void delete()
{
    #SKS_Global
    BankAccountTrans bankAccountTrans;
    ;

    if(this.hasMatch())
    {
        while select forupdate BankAccountTrans
            where BankAccountTrans.SKS_BR_ManualReconciliationID ==  this.ManualReconciliationID
        {
            if(bankAccountTrans.Included)
            {
                SKS_BR_Matching::UnclearCheck(BankAccountTrans);
            }
            BankAccountTrans.SKS_BR_ManualReconciliationID = str2guid(#EmptyGuidString);
            BankAccountTrans.SKS_BR_IntermMatch = NoYes::No;
            BankAccountTrans.update();
        }
    }
    super();
}

[SysClientCacheDataMethodAttribute(true)]
display CurrencyCode getOrigStatementCurrency()
{
    BankAccountStatement locBankAccountStatement;

    select firstOnly CurrencyCode from locBankAccountStatement where locBankAccountStatement.RecId == this.OriginalStatementRecId;
    return locBankAccountStatement.CurrencyCode;
}

