public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(SKS_BR_BankTran, InUse):
            this.unMarkUserPrivate();
            break;
    }
}

void unMarkUserPrivate()
{
    this.InUseBy    = '';
    this.InUse      = NoYes::No;
    this.SessionId  = 0;
    this.SessionLoginDateTime = utcdatetimenull();
}

