/// <summary>
/// SKS_BR5
/// </summary>
/// <param name="_bankTransRecId">
/// Bank transaction RecId.
/// </param>
/// <param name="_forUpdate">
/// Forupdate true or false.
/// </param>
/// <returns>
/// SKS_BR_BankTran object.
/// </returns>
public static SKS_BR_BankTran findByOriginalBankStmt(RefRecId _bankTransRecId, boolean _forUpdate = false)
{
    SKS_BR_BankTran sKS_BR_BankTran;

    if(_bankTransRecId)
    {
        if(_forUpdate)
        {
            sKS_BR_BankTran.selectForUpdate(_forUpdate);
        }
        select firstonly * from sKS_BR_BankTran
            where sKS_BR_BankTran.OriginalStatementRecId == _bankTransRecId;
    }
    return sKS_BR_BankTran;
}

public static client PmfTableLookupExtended lookupBankStatements(FormStringControl _lookupCtrl,  Name _filterRecord, dataAreaId _company = '', CompanyBankAccountId _bankAccount = '')
{
    Query                   query;
    QueryBuildDataSource    queryBuildDataSource;
    PmfTableLookupExtended  sysTableLookup;
    DictEnum                dictEnum = new DictEnum(enumnum(AllNotReconciled));
    AllNotReconciled        allNotReconciled =  dictEnum.name2Value(_filterRecord);
    Container               companyList = SKS_EB_VirtualCompany::getBankRecCompanies(); //7448
    int                     i; //7448

    // 7517 - change data sources to view - SKS_BR_BankAccountStatement
    sysTableLookup     = PmfTableLookupExtended::newParameters(tablenum(SKS_BR_BankAccountStatement),_lookupCtrl,false);
    sysTableLookup.addLookupfield(fieldnum(SKS_BR_BankAccountStatement,accountstatementNum),true);
    sysTableLookup.addLookupfield(fieldnum(SKS_BR_BankAccountStatement,accountstatementdate));
    sysTableLookup.addLookupfield(fieldnum(SKS_BR_BankAccountStatement,AccountId));
    sysTableLookup.addLookupfield(fieldnum(SKS_BR_BankAccountStatement,Id));
    sysTableLookup.addLookupfield(fieldnum(SKS_BR_BankAccountStatement,ReconcileDate));

    query = new Query();

    queryBuildDataSource = query.addDataSource(tablenum(SKS_BR_BankAccountStatement));
    //7448 -->
    query.allowCrossCompany(true);
    // 8702 CR -->
    if(_company || _bankAccount)
    {
        queryBuildDataSource.clearRanges();
        if(_company)
        {
            query.addCompanyRange(_company);
        }
        queryBuildDataSource.addRange(fieldNum(SKS_BR_BankAccountStatement,AccountId)).value(_bankAccount);
    }
    else
    {
        for (i=1; i <= conLen(companyList); i++)
        {
            query.addCompanyRange(conPeek(companyList, i));
        }
    }
    // 8702 CR <--
    //7448 <--
    //SKS_FDD_13903 - To make sure that we are showing only BR related statement into that lookup
    queryBuildDataSource.addRange(fieldNum(SKS_BR_BankAccountStatement, SKS_BR_BankHeaderRecId)).value(SysQuery::valueNot(0));
    queryBuildDataSource.addSortField(fieldnum(SKS_BR_BankAccountStatement,Accountstatementdate),SortOrder::Descending);

    if(allNotReconciled == AllNotReconciled::NotReconciled)
    {
        queryBuildDataSource.addRange(fieldnum(SKS_BR_BankAccountStatement,ReconcileDate)).value(queryvalue(dateNull()));
    }
    if(allNotReconciled == AllNotReconciled::Reconciled)
    {
        queryBuildDataSource.addRange(fieldnum(SKS_BR_BankAccountStatement,ReconcileDate)).value('!' + queryvalue(dateNull()));
    }
    sysTableLookup.parmQuery(query);
    sysTableLookup.parmUseLookupValue(true);

    sysTableLookup.performFormLookup();

    return sysTableLookup;
}

/// <summary>
/// Get GL posting date based on GL posting date setup
/// </summary>
///  CR-7653
public TransDate getGLPostingDate()
{
    SKS_PP_Bank sks_PP_Bank;
    SKS_BR_FileFormat sks_br_fileformat;
    TransDate retPostingDate;

    changecompany(this.company())
    {
        sks_PP_Bank = SKS_PP_Bank::find(this.AccountId);
        select GJPostingDate, RecId from sks_br_fileformat where sks_br_fileformat.FormatID == sks_PP_Bank.BRFormatID;

        if(sks_br_fileformat.RecId)
        {
            if(sks_br_fileformat.GJPostingDate == SKS_BR_GJPostingDate::ImportedStatementDate)
            {
                retPostingDate = this.ImportedStatementDate;
            }
            else
            {
                retPostingDate = this.TranDate;
            }
        }

        if(retPostingDate == Global::datenull())
        {
            retPostingDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
        }
    }

    return retPostingDate;
}

/// <summary>
/// Determines whether the current record is valid and ready to be written to the database.
/// </summary>
/// <returns>
///  true if the record is valid; otherwise, false.
/// </returns>
/// SKS 9344
public boolean validateWrite()
{
    boolean ret;

    ret = super();

    changecompany(this.Company())
    {
        if (!CustTable::exist(this.CustAccount) && this.CustAccount != '')
        {
            ret = checkFailed(strFmt("@SKS:SKGBRValidateAccntNum", this.CustAccount));
        }
    }

    return ret;
}

/// <summary>
///  Get bank transaction currency
/// </summary>
/// <returns>Returns currency code</returns>
//9479
public CurrencyCode getCurrencyCode()
{
    BankAccountTable        bankAccountTable;

    changecompany(this.company())
    {
        select CurrencyCode, DataAreaId from bankAccountTable
            where bankAccountTable.AccountID == this.AccountId;
    }
    return bankAccountTable.CurrencyCode;
}

