/// <summary>
/// get the imported journal name
/// </summary>
/// <returns>LedgerJournalNameId</returns>
public LedgerJournalNameId getJournalName()
{
    LedgerJournalNameId JournalName = SKS_BR_Fileformattranmapping::FindByTranType(this.FileTranType, this.accountid, this.GLMappingRuleGroupId).TranTypeJournalName;

    if(JournalName == '')
    {
        return this.JournalName;
    }
    else
    {
        return JournalName;
    }
}

/// <summary>
/// get the manual journal name - Devops - 13662
/// </summary>
/// <returns>LedgerJournalNameId</returns>
public LedgerJournalNameId getManualJournalName()
{
    LedgerJournalNameId JournalName = SKS_BR_Fileformattranmapping::FindByTranType(this.FileTranType, this.accountid).TranTypeManualJournalName;

    if(JournalName == '')
    {
        return this.ManualJournalName;
    }
    else
    {
        return JournalName;
    }
}

