public void PopulateOnAdditionalFields()
{
    this.JournalName = this.getJournalName();
    this.ManualJournalName = this.getManualJournalName(); //DevOps - 13662
    this.TranDescriptionShort = this.TranDescriptionShort == '' ? this.TranDescription : this.TranDescriptionShort;
}
