public static SKS_EFTAR_Bank find(BankAccountId _bankAccountId, PaymMode _paymMode,
                                 boolean            _forUpdate = false)
{
    SKS_EFTAR_Bank sks_eftar_bank;

    if (_bankAccountId)
    {
        if (_forUpdate)
        {
            sks_eftar_bank.selectForUpdate(_forUpdate);
        }

        select sks_eftar_bank
            where sks_eftar_bank.BankAccountId == _bankAccountId &&
                  sks_eftar_bank.PaymMode == _paymMode;
    }

    return sks_eftar_bank;
}

static server void UpdateFromSetup(SKS_EFT_Setup _sks_eft_setup)
{
    SKS_EFTAR_Bank sks_eftar_bankUpdate;
    SKS_EFT_FileFormat sks_eft_FileFormat;
    CustPaymModeTable custPaymModeTable;
    CustPaymMode lastpaymMode;
    container       mutliCompanies; //FDD-10590
    ;
    while select forupdate sks_eftar_bankUpdate order by PaymMode
    {
        if(sks_eftar_bankUpdate.PaymMode != lastpaymMode)
        {
            select SKS_EFT_FormatId from custPaymModeTable
                    where custPaymModeTable.PaymMode == sks_eftar_bankUpdate.PaymMode
                join PreNote, BlockFill, CrLf, IncludeCpnyCredit from sks_eft_FileFormat
                    where sks_eft_FileFormat.FormatId == custPaymModeTable.SKS_EFT_FormatId;
        }

        if(sks_eftar_bankUpdate.AREMFromSetup)
        {
            sks_eftar_bankUpdate.AREMAttachmentName = _sks_eft_setup.AREMAttachmentName;
            sks_eftar_bankUpdate.AREMReplyToAddr = _sks_eft_setup.AREMReplyToAddr;
            sks_eftar_bankUpdate.AREMReplytoName = _sks_eft_setup.AREMReplytoName;
            sks_eftar_bankUpdate.AREMCompanyName = _sks_eft_setup.AREMCompanyName;
            sks_eftar_bankUpdate.AREMTemplateAttach = _sks_eft_setup.AREMTemplateAttach;
            sks_eftar_bankUpdate.AREMTemplateNoAttach = _sks_eft_setup.AREMTemplateNoAttach;
            sks_eftar_bankUpdate.AREMTemplateLanguage = _sks_eft_setup.AREMTemplateLanguage;
            sks_eftar_bankUpdate.ARAENTemplateAttach = _sks_eft_setup.ARAENTemplateAttach;
            sks_eftar_bankUpdate.ARAENTemplateNoAttach = _sks_eft_setup.ARAENTemplateNoAttach;
        }

        if(sks_eftar_bankUpdate.ARFileFromSetup)
        {
            sks_eftar_bankUpdate.ARFileName = _sks_eft_setup.ARFileName;
            sks_eftar_bankUpdate.ARArchive = _sks_eft_setup.ARArchive;
            sks_eftar_bankUpdate.ARPathArchive = _sks_eft_setup.ARPathArchive;
            sks_eftar_bankUpdate.ARFilePath = _sks_eft_setup.ARFilePath;
            sks_eftar_bankUpdate.ARBankFabricFileLocation = _sks_eft_setup.ARBankFabricFileLocation;
            sks_eftar_bankUpdate.ARHeadTrailID = _sks_eft_setup.ARHeadTrailID;
            sks_eftar_bankUpdate.ARACHInclCR = _sks_eft_setup.ARInclCR;
            sks_eftar_bankUpdate.ARDelete = _sks_eft_setup.ARDelete;
            sks_eftar_bankUpdate.ARCommType = _sks_eft_setup.ARCommType;
            sks_eftar_bankUpdate.ARCommUserProg = _sks_eft_setup.ARCommUserProg;
            sks_eftar_bankUpdate.BankHolidayGroupId = _sks_eft_setup.ARBankHolidayGroupID;
            sks_eftar_bankUpdate.CharacterReplacementGroupId = _sks_eft_setup.ARCRGroupId;
            sks_eftar_bankUpdate.ARCommID = _sks_eft_setup.ARCommID;

            if(_sks_eft_setup.ARReqPreNote && sks_eft_FileFormat.PreNote)
            {
                sks_eftar_bankUpdate.ARACHPreNote = NoYes::Yes;
            }
            else
            {
                sks_eftar_bankUpdate.ARACHPreNote = NoYes::No;
            }

            if(_sks_eft_setup.ARInclCR && sks_eft_FileFormat.IncludeCpnyCredit)
            {
                sks_eftar_bankUpdate.ARACHInclCR = NoYes::Yes;
            }
            else
            {
                sks_eftar_bankUpdate.ARACHInclCR = NoYes::No;
            }
        }
        else
        {
            if(!sks_eft_FileFormat.PreNote)
            {
                sks_eftar_bankUpdate.ARACHPreNote = NoYes::No;
            }

            if(!sks_eft_FileFormat.IncludeCpnyCredit)
            {
                sks_eftar_bankUpdate.ARACHInclCR = NoYes::No;
            }
        }

        if(sks_eftar_bankUpdate.CompanyFromSetup)
        {
            sks_eftar_bankUpdate.ACHCompanyID = _sks_eft_setup.ACHCompanyID;
            sks_eftar_bankUpdate.ACHCompanyName = _sks_eft_setup.ACHCompanyName;
            sks_eftar_bankUpdate.ACHOrigDFI = _sks_eft_setup.ACHOrigDFI;
            sks_eftar_bankUpdate.ACHOrigDFIName = _sks_eft_setup.ACHOrigDFIName;
            sks_eftar_bankUpdate.ACHFileIDModifier = _sks_eft_setup.ACHFileIDModifier;
        }

        if(sks_eftar_bankUpdate.FileFromSetup)
        {
            sks_eftar_bankUpdate.ACHImmDest = _sks_eft_setup.ACHImmDest;
            sks_eftar_bankUpdate.ACHImmDestName = _sks_eft_setup.ACHImmDestName;
            sks_eftar_bankUpdate.ACHImmOrig = _sks_eft_setup.ACHImmOrig;
            sks_eftar_bankUpdate.ACHImmOrigName = _sks_eft_setup.ACHImmOrigName;

            if(_sks_eft_setup.FillBlock && sks_eft_FileFormat.BlockFill)
            {
                sks_eftar_bankUpdate.FillBlock = NoYes::Yes;
            }
            else
            {
                sks_eftar_bankUpdate.FillBlock = NoYes::No;
            }

            if(_sks_eft_setup.FileCrLf && sks_eft_FileFormat.CrLf)
            {
                sks_eftar_bankUpdate.FileCrLf = NoYes::Yes;
            }
            else
            {
                sks_eftar_bankUpdate.FileCrLf = NoYes::No;
            }
        }
        else
        {
            if(!sks_eft_FileFormat.BlockFill)
            {
                sks_eftar_bankUpdate.FillBlock = NoYes::No;
            }

            if(!sks_eft_FileFormat.CrLf)
            {
                sks_eftar_bankUpdate.FileCrLf = NoYes::no;
            }
        }

        if(sks_eftar_bankUpdate.PostedPaymSetupDefaults)
        {
            sks_eftar_bankUpdate.AllowPostedPmtReversal = _sks_eft_setup.SKS_DD_AllowPostedPmtJournal;
            sks_eftar_bankUpdate.RevJournalName = _sks_eft_setup.SKS_DD_RevJournalName;
            sks_eftar_bankUpdate.RevPaymMode = _sks_eft_setup.SKS_DD_RevPaymMode;
            sks_eftar_bankUpdate.AutoPostPaymentJournal = _sks_eft_setup.SKS_DD_AutoPostJournal;
        }

        sks_eftar_bankUpdate.update();

        lastpaymMode = sks_eftar_bankUpdate.PaymMode;
    }
}

    /// <summary>
    /// Validating the inbound file path for Direct debit.
    /// </summary>
    public boolean SKS_DD_FileLocationValide(SKS_EFTAR_Bank _SKS_EFTAR_Bank)
    {
        boolean ret = true;

        //<SKS-begin>
        SKS_EFT_InboundFileGroups   sks_EFT_InboundFileGroupsloc;
        SKS_EFT_InboundFileFormat   sks_EFT_InboundFileFormatloc;
        SKS_EB_CommProcess          sks_EB_CommProcessloc;
        SKS_EFT_Bank                sks_EFT_Bankloc;
        SKS_EFT_InboundFileGroupId  sks_EFT_InboundFileGroupIdloc;
        SKS_EFTBTB_Bank             sks_EFTBTB_Bankloc;
        SKS_EFTCNP_Bank             sks_EFTCNP_Bankloc;
        //</SKS-end>


        //<SKS-begin>
        SKS_EB_FileStorage  sks_EB_FileStorage;

        //BankFabric
        select firstonly StorageOption_APEFTFXDD from sks_EB_FileStorage;

        //<SKS-begin>
        if ((_SKS_EFTAR_Bank.SKS_DD_InboundFileLoc != '' && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue) ||
             (_SKS_EFTAR_Bank.ARBankFabricInboundFileLoc != '' &&  sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue))
        {
            sks_EFT_InboundFileGroupIdloc = SKS_EFT_FileFormat::find(CustPaymModeTable::find(_SKS_EFTAR_Bank.PaymMode).SKS_EFT_FormatId).SKS_DD_InboundFileGroupId;

            select RecId from sks_EFT_InboundFileGroupsloc
                where sks_EFT_InboundFileGroupsloc.InboundFileGroupId  == sks_EFT_InboundFileGroupIdloc
            join FormatId,SKS_DD_Modules from sks_EFT_InboundFileFormatloc
                where sks_EFT_InboundFileFormatloc.FormatId == sks_EFT_InboundFileGroupsloc.InboundFormatId1;

            select Module from sks_EB_CommProcessloc
                where sks_EB_CommProcessloc.CommID == _SKS_EFTAR_Bank.SKS_DD_InCommId;

            switch  (sks_EFT_InboundFileFormatloc.SKS_DD_Modules)
            {
                case SKS_DD_Modules::APEFT:
                    /*  if (sks_EB_CommProcessloc.Module != SKS_EB_Module::APEFT)
                    {
                    select firstonly sks_EFT_Bankloc
                    where sks_EFT_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                    && sks_EFT_Bankloc.InboundFileLoc == _SKS_EFTAR_Bank.SKS_DD_InboundFileLoc;

                    if (sks_EFT_Bankloc)
                    {
                    error(strFmt("Invalid path. This folder already used for APEFT+BankAccount  %3. The file format %1 is for %2 module", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFT_Bankloc.PaymMode));
                    ret = false;
                    }
                    }*/
                    break;
                case SKS_DD_Modules::Directdebit:
                    /* if (sks_EB_CommProcessloc.Module != SKS_EB_Module::AREFT)
                    {
                    error("Invalid path");
                    ret = false;
                    }*/
                    break;
                case SKS_DD_Modules::APEFT_DDSeparatePath:
                    if (sks_EB_CommProcessloc.Module == SKS_EB_Module::APEFT)
                    {
                        /*select firstonly sks_EFTAR_Bankloc
                        where sks_EFTAR_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                        && sks_EFTAR_Bankloc.SKS_DD_InboundFileLoc == _SKS_EFT_Bank.InboundFileLoc;

                        if (sks_EFTAR_Bankloc)
                        {
                        ret = checkFailed(strFmt("Invalid path. This folder already used for AREFT+BankAccount %3. The file format %1 is for %2 module", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFT_Bankloc.PaymMode));
                        }*/
                    }
                    else
                    {
                        if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                        {
                            select firstonly sks_EFT_Bankloc
                            where sks_EFT_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                            && sks_EFT_Bankloc.APBankFabricInboundFileLocation == _SKS_EFTAR_Bank.ARBankFabricInboundFileLoc;
                        }
                        else
                        {
                            select firstonly sks_EFT_Bankloc
                            where sks_EFT_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                            && sks_EFT_Bankloc.InboundFileLoc == _SKS_EFTAR_Bank.SKS_DD_InboundFileLoc;
                        }

                        if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                        {
                            select firstonly sks_EFTBTB_Bankloc
                            where sks_EFTBTB_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                            && sks_EFTBTB_Bankloc.BTBBankFabricInboundFileLocation == _SKS_EFTAR_Bank.ARBankFabricInboundFileLoc;
                        }
                        else
                        {
                            select firstonly sks_EFTBTB_Bankloc
		                    where sks_EFTBTB_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
		                    && sks_EFTBTB_Bankloc.BTBInboundFileLoc == _SKS_EFTAR_Bank.SKS_DD_InboundFileLoc;
                        }

                        if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                        {
                            select firstonly sks_EFTCNP_Bankloc
                            where sks_EFTCNP_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                            && sks_EFTCNP_Bankloc.CNPBankFabricInboundFileLocation == _SKS_EFTAR_Bank.ARBankFabricInboundFileLoc;
                        }
                        else
                        {
                            select firstonly sks_EFTCNP_Bankloc
                            where sks_EFTCNP_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                            && sks_EFTCNP_Bankloc.CNPInboundFileLoc == _SKS_EFTAR_Bank.SKS_DD_InboundFileLoc;
                        }


                        if (sks_EFT_Bankloc)
                        {
                            ret = checkFailed(strFmt("@SKS6138", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFT_Bankloc.PaymMode));
                        }
                        if (sks_EFTBTB_Bankloc)
                        {
                            ret = checkFailed(strFmt("@SKS:SKGBTBInvalidPath", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTBTB_Bankloc.PaymMode));
                        }
                        if (sks_EFTCNP_Bankloc)
                        {
                            ret = checkFailed(strFmt("@SKS:SKGCNPInvalidPath", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTCNP_Bankloc.PaymMode));
                        }
                    }
                    break;
                case SKS_DD_Modules::APEFT_DDSharedPath:
                    if (sks_EB_CommProcessloc.Module != SKS_EB_Module::APEFT && sks_EB_CommProcessloc.Module != SKS_EB_Module::AREFT)
                    {
                        ret = checkFailed(strFmt("@SKS6139"));
                    }
                    break;
                default :
                    break;
            }
        }
        //</SKS-end>

        return ret;

    }

/// <summary>
///    Finds the specified record in the <c>SKS_EFTAR_Bank</c> table.
/// </summary>
/// <param name="_company">
///    The company of the payment method.
/// </param>
/// <param name="_bankAccountId">
///    The customer bank account.
/// </param>
/// <param name="_paymModeId">
///    The ID of the payment method.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
///    A record in the <c>SKS_EFTAR_Bank</c> table; otherwise, an empty record.
/// </returns>
public static SKS_EFTAR_Bank findByCompany(DataAreaId _company,
                                           BankAccountId _bankAccountId, 
                                           PaymMode _paymMode,
                                           boolean            _forUpdate = false)
{
    SKS_EFTAR_Bank  sks_eftar_Bank;

    if(_company)
    {
        changecompany(_company)
        {
            sks_eftar_Bank = SKS_EFTAR_Bank::find(_bankAccountId, _paymMode, _forUpdate);
        }
    }

    return sks_eftar_Bank;
}

/// <summary>
/// Inserting the bank code lookups
/// </summary>
public void insert()
{
    CustPaymModeTable       custPaymModeTable;
    SKS_EFT_FileFormat      lSKS_EFT_FileFormat;
    ;

    ttsbegin;
    if(this.PaymMode != '')
    {
        custPaymModeTable = CustPaymModeTable::find(this.PaymMode);
    }

    super();

    select firstOnly RecId from lSKS_EFT_FileFormat
        where lSKS_EFT_FileFormat.FormatId == custPaymModeTable.SKS_EFT_FormatId;

    this.BankAccountLookupAddition(lSKS_EFT_FileFormat);

    ttscommit;
}

/// <summary>
/// Inserting the bank code lookups
/// </summary>
private void BankAccountLookupAddition(SKS_EFT_FileFormat SKS_EFT_FileFormat)
{
    SKS_EFT_ARBankAccountPaymLookup         lSKS_EFT_ARBankAccountPaymLookup;
    SKS_EFT_FileFormatFields                SKS_EFT_FileFormatFields;
    RecordInsertList                        ril;

    ril = new RecordInsertList(lSKS_EFT_ARBankAccountPaymLookup.TableId);

    while select RecId, fixedvalue, defaultcodelookupvaluesrecid from SKS_EFT_FileFormatFields
        where SKS_EFT_FileFormatFields.fileformatrecid == SKS_EFT_FileFormat.recid
    {
        lSKS_EFT_ARBankAccountPaymLookup.CodeLookupValuesRecId = SKS_EFT_FileFormatFields.DefaultCodeLookupValuesRecId;
        lSKS_EFT_ARBankAccountPaymLookup.FileFormatFieldsRecId = SKS_EFT_FileFormatFields.RecId;
        lSKS_EFT_ARBankAccountPaymLookup.BankPaymRecId = this.RecId;
        lSKS_EFT_ARBankAccountPaymLookup.FixedValue = SKS_EFT_FileFormatFields.FixedValue;
        ril.add(lSKS_EFT_ARBankAccountPaymLookup);
    }
    ril.insertDatabase();
}
