public static SKS_EFTCNP_Bank find(BankAccountId _bankAccountId, PaymMode _paymMode,
                                 boolean            _forUpdate = false)
{
    SKS_EFTCNP_Bank sks_eft_bank;

    if (_bankAccountId)
    {
        if (_forUpdate)
        {
            sks_eft_bank.selectForUpdate(_forUpdate);
        }

        select sks_eft_bank
            where sks_eft_bank.BankAccountId == _bankAccountId &&
                  sks_eft_bank.PaymMode == _paymMode;
    }

    return sks_eft_bank;
}

static server void UpdateFromSetup(SKS_EFT_Setup _sks_eft_setup)
{
    SKS_EFTCNP_Bank sks_eftcnp_bankUpdate;
    SKS_EFT_FileFormat sks_eft_FileFormat;
    CustPaymModeTable   loccustpaymmodetable;
    PaymMode lastpaymMode;
    ;
    while select forupdate sks_eftcnp_bankUpdate order by PaymMode
    {
        if(sks_eftcnp_bankUpdate.PaymMode != lastpaymMode)
        {
            select firstonly loccustpaymmodetable
                where loccustpaymmodetable.PaymMode == sks_eftcnp_bankUpdate.PaymMode
                join PreNote, BlockFill, CrLf, IncludeCpnyDebit from sks_eft_FileFormat
                where sks_eft_FileFormat.FormatId == loccustpaymmodetable.SKS_EFT_FormatId;
        }

        if(sks_eftcnp_bankUpdate.CNPFileFromSetup)
        {
            sks_eftcnp_bankUpdate.CNPFileName = _sks_eft_setup.CNPFileName;
            sks_eftcnp_bankUpdate.CNPFilePath = _sks_eft_setup.CNPFilePath;
            sks_eftcnp_bankUpdate.CNPBankFabricFileLocation = _sks_eft_setup.CNPBankFabricFileLocation;
            sks_eftcnp_bankUpdate.CNPArchive = _sks_eft_setup.CNPArchive;
            sks_eftcnp_bankUpdate.CNPPathArchive = _sks_eft_setup.CNPPathArchive;
            sks_eftcnp_bankUpdate.CNPHeadTrailID = _sks_eft_setup.CNPHeadTrailID;
            sks_eftcnp_bankUpdate.CNPACHInclDR = _sks_eft_setup.CNPIncludeOffsetCompany;
            sks_eftcnp_bankUpdate.CNPDelete = _sks_eft_setup.CNPDelete;
            //sks_eftcnp_bankUpdate.CNPCommType = _sks_eft_setup.APCommType;
            sks_eftcnp_bankUpdate.CNPCommID = _sks_eft_setup.CNPCommID;
            //sks_eftcnp_bankUpdate.CNPCommUserProg = _sks_eft_setup.APCommUserProg;
            sks_eftcnp_bankUpdate.BankHolidayGroupId = _sks_eft_setup.CNPBankHolidayGroupID;
            sks_eftcnp_bankUpdate.CharacterReplacementGroupId = _sks_eft_setup.CNPCRGroupId;

        }

        if(sks_eftcnp_bankUpdate.CompanyFromSetup)
        {
            sks_eftcnp_bankUpdate.ACHCompanyID = _sks_eft_setup.ACHCompanyID;
            sks_eftcnp_bankUpdate.ACHCompanyName = _sks_eft_setup.ACHCompanyName;
            sks_eftcnp_bankUpdate.ACHOrigDFI = _sks_eft_setup.ACHOrigDFI;
            sks_eftcnp_bankUpdate.ACHOrigDFIName = _sks_eft_setup.ACHOrigDFIName;
            sks_eftcnp_bankUpdate.ACHFileIDModifier = _sks_eft_setup.ACHFileIDModifier;
        }

        sks_eftcnp_bankUpdate.update();

        lastpaymMode = sks_eftcnp_bankUpdate.PaymMode;
    }
}

/// <summary>
/// Validating the inbound file path for AP.
/// </summary>
public boolean SKS_DD_FileLocationValide(SKS_EFTCNP_Bank _SKS_EFTCNP_Bank)
{
    boolean ret = true;

    //<SKS-begin>
    SKS_EFT_InboundFileGroups   sks_EFT_InboundFileGroupsloc;
    SKS_EFT_InboundFileFormat   sks_EFT_InboundFileFormatloc;
    SKS_EB_CommProcess          sks_EB_CommProcessloc;
    SKS_EFT_InboundFileGroupId  sks_EFT_InboundFileGroupIdloc;
    SKS_EFTAR_Bank              sks_EFTAR_Bankloc;
    SKS_EFT_Bank              sks_EFTAP_Bankloc;
    SKS_EFTBTB_Bank             sks_EFTBTB_Bankloc;
    //</SKS-end>
    SKS_EB_FileStorage  sks_EB_FileStorage;

    //BankFabric
    select firstonly StorageOption_APEFTFXDD from sks_EB_FileStorage;

    if ((_SKS_EFTCNP_Bank.CNPInboundFileLoc != '' && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue) ||
         (_SKS_EFTCNP_Bank.CNPBankFabricInboundFileLocation != '' &&  sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue))
    {
        sks_EFT_InboundFileGroupIdloc = SKS_EFT_FileFormat::find(CustPaymModeTable::find(_SKS_EFTCNP_Bank.PaymMode).SKS_CNP_FormatId).SKS_CNP_InboundFileGroupId;

        select firstonly sks_EFT_InboundFileGroupsloc
            where sks_EFT_InboundFileGroupsloc.InboundFileGroupId  == sks_EFT_InboundFileGroupIdloc
        join FormatId,SKS_DD_Modules from sks_EFT_InboundFileFormatloc
            where sks_EFT_InboundFileFormatloc.FormatId == sks_EFT_InboundFileGroupsloc.InboundFormatId1;

        select Module from sks_EB_CommProcessloc
            where sks_EB_CommProcessloc.CommID == _SKS_EFTCNP_Bank.InboundCommID;

        switch  (sks_EFT_InboundFileFormatloc.SKS_DD_Modules)
        {
            case SKS_DD_Modules::APEFT:
                /* if (sks_EB_CommProcessloc.Module != SKS_EB_Module::APEFT)
                {
                error("Invalid path");
                ret = false;
                }*/
                break;
            case SKS_DD_Modules::Directdebit:
                /* if (sks_EB_CommProcessloc.Module != SKS_EB_Module::AREFT)
                {
                select firstonly sks_EFTAR_Bankloc
                where sks_EFTAR_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                && sks_EFTAR_Bankloc.SKS_DD_InboundFileLoc == _SKS_EFT_Bank.InboundFileLoc;

                if (sks_EFTAR_Bankloc)
                {
                error(strFmt("Invalid path. This folder already used for AREFT+BankAccount  %3. The file format %1 is for %2 module", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTAR_Bankloc.PaymMode));
                ret = false;
                }
                }*/
                break;
            case SKS_DD_Modules::APEFT_DDSeparatePath:
                if (sks_EB_CommProcessloc.Module == SKS_EB_Module::APEFT || sks_EB_CommProcessloc.Module == SKS_EB_Module::AREFT)
                {
                    /*select firstonly sks_EFT_Bankloc
                    where sks_EFT_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                    && sks_EFT_Bankloc.InboundFileLoc == _SKS_EFTAR_Bank.SKS_DD_InboundFileLoc;
                    if (sks_EFT_Bankloc)
                    {
                    ret = checkFailed(strFmt("Invalid path. This folder already used for APEFT+BankAccount %3. The file format %1 is for %2 module", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFT_Bankloc.PaymMode));
                    }*/
                }
                else
                {
                    if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                    {
                        select firstonly sks_EFTAP_Bankloc
                        where sks_EFTAP_Bankloc.BankAccountId == _SKS_EFTCNP_Bank.BankAccountId
                        && sks_EFTAP_Bankloc.APBankFabricInboundFileLocation == _SKS_EFTCNP_Bank.CNPBankFabricInboundFileLocation;
                    }
                    else
                    {
                        select firstonly sks_EFTAP_Bankloc
                        where sks_EFTAP_Bankloc.BankAccountId == _SKS_EFTCNP_Bank.BankAccountId
                        && sks_EFTAP_Bankloc.InboundFileLoc == _SKS_EFTCNP_Bank.CNPInboundFileLoc;
                    }

                    if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                    {
                        select firstonly sks_EFTAR_Bankloc
                        where sks_EFTAR_Bankloc.BankAccountId == _SKS_EFTCNP_Bank.BankAccountId
                        && sks_EFTAR_Bankloc.ARBankFabricInboundFileLoc == _SKS_EFTCNP_Bank.CNPBankFabricInboundFileLocation;
                    }
                    else
                    {
                        select firstonly sks_EFTAR_Bankloc
                        where sks_EFTAR_Bankloc.BankAccountId == _SKS_EFTCNP_Bank.BankAccountId
                        && sks_EFTAR_Bankloc.SKS_DD_InboundFileLoc == _SKS_EFTCNP_Bank.CNPInboundFileLoc;
                    }

                    if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                    {
                        select firstonly sks_EFTBTB_Bankloc
                        where sks_EFTBTB_Bankloc.BankAccountId == _SKS_EFTCNP_Bank.BankAccountId
                        && sks_EFTBTB_Bankloc.BTBBankFabricInboundFileLocation == _SKS_EFTCNP_Bank.CNPBankFabricInboundFileLocation;
                    }
                    else
                    {
                        select firstonly sks_EFTBTB_Bankloc
                        where sks_EFTBTB_Bankloc.BankAccountId == _SKS_EFTCNP_Bank.BankAccountId
                        && sks_EFTBTB_Bankloc.BTBInboundFileLoc == _SKS_EFTCNP_Bank.CNPInboundFileLoc;
                    }

                    if (sks_EFTAR_Bankloc)
                    {
                        ret = checkFailed(strFmt("@SKS6137", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTAR_Bankloc.PaymMode));
                    }
                    if(sks_EFTAP_Bankloc)
                    {
                        ret = checkFailed(strFmt("@SKS6138", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTAP_Bankloc.PaymMode));
                    }
                    if (sks_EFTBTB_Bankloc)
                    {
                        ret = checkFailed(strFmt("@SKS:SKGBTBInvalidPath", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTBTB_Bankloc.PaymMode));
                    }
                }
                break;
            case SKS_DD_Modules::APEFT_DDSharedPath:
                if (sks_EB_CommProcessloc.Module != SKS_EB_Module::APEFT && sks_EB_CommProcessloc.Module != SKS_EB_Module::AREFT &&
                    sks_EB_CommProcessloc.Module != SKS_EB_Module::BankToBankTransfer && sks_EB_CommProcessloc.Module != SKS_EB_Module::CustomerReimbursements)
                {
                    ret = checkFailed(strFmt("@SKS6139"));
                }
                break;
            default :
                break;
        }
    }
    //</SKS-end>

    return ret;

}

// SKS_FDD_10590 -->
public static SKS_EFTCNP_Bank findByCompany(CompanyId _company, BankAccountId _bankAccountId, PaymMode _paymMode, boolean _forUpdate = false)
{
    SKS_EFTCNP_Bank sks_eft_bank;

    if (_company)
    {
        changecompany(_company)
        {
            sks_eft_bank = SKS_EFTCNP_Bank::find(_bankAccountId, _paymMode, _forUpdate);
        }
    }

    return sks_eft_bank;
}

// SKS_FDD_10590 <--

/// <summary>
/// Inserting the bank code lookups
/// </summary>
public void insert()
{
    CustPaymModeTable       lCustPaymModeTable;
    SKS_EFT_FileFormat      lSKS_EFT_FileFormat;
    ;

    ttsbegin;
    if(this.PaymMode != '')
    {
        select * from lCustPaymModeTable where lCustPaymModeTable.PaymMode == this.PaymMode && lCustPaymModeTable.SKS_CNP_PaymMode == NoYes::Yes;
    }

    super();

    select firstOnly RecId from lSKS_EFT_FileFormat
        where lSKS_EFT_FileFormat.FormatId == lCustPaymModeTable.SKS_CNP_FormatId;

    this.BankAccountLookupAddition(lSKS_EFT_FileFormat);

    ttscommit;
}

/// <summary>
/// Inserting the bank code lookups
/// </summary>
private void BankAccountLookupAddition(SKS_EFT_FileFormat SKS_EFT_FileFormat)
{
    SKS_EFT_CNPBankAccountPaymLookup       lSKS_EFT_CNPBankAccountPaymLookup;
    SKS_EFT_FileFormatFields            SKS_EFT_FileFormatFields;
    RecordInsertList                    ril;

    ril = new RecordInsertList(lSKS_EFT_CNPBankAccountPaymLookup.TableId);

    while select RecId, fixedvalue, defaultcodelookupvaluesrecid from SKS_EFT_FileFormatFields
        where SKS_EFT_FileFormatFields.fileformatrecid == SKS_EFT_FileFormat.recid
    {
        lSKS_EFT_CNPBankAccountPaymLookup.CodeLookupValuesRecId = SKS_EFT_FileFormatFields.DefaultCodeLookupValuesRecId;
        lSKS_EFT_CNPBankAccountPaymLookup.FileFormatFieldsRecId = SKS_EFT_FileFormatFields.RecId;
        lSKS_EFT_CNPBankAccountPaymLookup.BankPaymRecId = this.RecId;
        lSKS_EFT_CNPBankAccountPaymLookup.FixedValue = SKS_EFT_FileFormatFields.FixedValue;
        ril.add(lSKS_EFT_CNPBankAccountPaymLookup);
    }
    ril.insertDatabase();
}
