public static SKS_EFT_Bank find(BankAccountId _bankAccountId, PaymMode _paymMode,
                                 boolean            _forUpdate = false)
{
    SKS_EFT_Bank sks_eft_bank;

    if (_bankAccountId)
    {
        if (_forUpdate)
        {
            sks_eft_bank.selectForUpdate(_forUpdate);
        }

        select sks_eft_bank
            where sks_eft_bank.BankAccountId == _bankAccountId &&
                  sks_eft_bank.PaymMode == _paymMode;
    }

    return sks_eft_bank;
}

static server void UpdateFromSetup(SKS_EFT_Setup _sks_eft_setup)
{
    SKS_EFT_Bank sks_eft_bankUpdate;
    SKS_EFT_FileFormat sks_eft_FileFormat;
    VendPaymModeTable vendPaymModeTable;
    VendPaymMode lastpaymMode;
    ;
    while select forupdate sks_eft_bankUpdate order by PaymMode
    {
        if(sks_eft_bankUpdate.PaymMode != lastpaymMode)
        {
            select SKS_EFT_FormatId from vendPaymModeTable
                where vendPaymModeTable.PaymMode == sks_eft_bankUpdate.PaymMode
            join PreNote, BlockFill, CrLf, IncludeCpnyDebit from sks_eft_FileFormat
                where sks_eft_FileFormat.FormatId == vendPaymModeTable.SKS_EFT_FormatId;
        }

        if(sks_eft_bankUpdate.APEMFromSetup)
        {
            sks_eft_bankUpdate.APEMAttachmentName = _sks_eft_setup.APEMAttachmentName;
            sks_eft_bankUpdate.APEMReplyToAddr = _sks_eft_setup.APEMReplyToAddr;
            sks_eft_bankUpdate.APEMReplytoName = _sks_eft_setup.APEMReplytoName;
            sks_eft_bankUpdate.APEMCompanyName = _sks_eft_setup.APEMCompanyName;
            sks_eft_bankUpdate.APEMTemplateAttach = _sks_eft_setup.APEMTemplateAttach;
            sks_eft_bankUpdate.APEMTemplateNoAttach = _sks_eft_setup.APEMTemplateNoAttach;
            sks_eft_bankUpdate.APEMTemplateLanguage = _sks_eft_setup.APEMTemplateLanguage;
        }

        if(sks_eft_bankUpdate.APFileFromSetup)
        {
            sks_eft_bankUpdate.APFileName = _sks_eft_setup.APFileName;
            sks_eft_bankUpdate.APFilePath = _sks_eft_setup.APFilePath;
            sks_eft_bankUpdate.APBankFabricFileLocation = _sks_eft_setup.APBankFabricFileLocation;
            sks_eft_bankUpdate.APArchive = _sks_eft_setup.APArchive;
            sks_eft_bankUpdate.APPathArchive = _sks_eft_setup.APPathArchive;
            sks_eft_bankUpdate.APHeadTrailID = _sks_eft_setup.APHeadTrailID;
            sks_eft_bankUpdate.APACHInclDR = _sks_eft_setup.APInclDR;
            sks_eft_bankUpdate.APDelete = _sks_eft_setup.APDelete;
            sks_eft_bankUpdate.APCommType = _sks_eft_setup.APCommType;
            sks_eft_bankUpdate.APCommID = _sks_eft_setup.APCommID;
            sks_eft_bankUpdate.APCommUserProg = _sks_eft_setup.APCommUserProg;
            sks_eft_bankUpdate.BankHolidayGroupId = _sks_eft_setup.APBankHolidayGroupID;
            sks_eft_bankUpdate.CharacterReplacementGroupId = _sks_eft_setup.APCRGroupId;

            if(_sks_eft_setup.APReqPreNote && sks_eft_FileFormat.PreNote)
            {
                sks_eft_bankUpdate.APACHPreNote = NoYes::Yes;
            }
            else
            {
                sks_eft_bankUpdate.APACHPreNote = NoYes::No;
            }

            if(_sks_eft_setup.APInclDR && sks_eft_FileFormat.IncludeCpnyDebit)
            {
                sks_eft_bankUpdate.APACHInclDR = NoYes::Yes;
            }
            else
            {
                sks_eft_bankUpdate.APACHInclDR = NoYes::No;
            }
        }
        else
        {
            if(!sks_eft_FileFormat.PreNote)
            {
                sks_eft_bankUpdate.APACHPreNote = NoYes::No;
            }

            if(!sks_eft_FileFormat.IncludeCpnyDebit)
            {
                sks_eft_bankUpdate.APACHInclDR = NoYes::No;
            }
        }

        if(sks_eft_bankUpdate.CompanyFromSetup)
        {
            sks_eft_bankUpdate.ACHCompanyID = _sks_eft_setup.ACHCompanyID;
            sks_eft_bankUpdate.ACHCompanyName = _sks_eft_setup.ACHCompanyName;
            sks_eft_bankUpdate.ACHOrigDFI = _sks_eft_setup.ACHOrigDFI;
            sks_eft_bankUpdate.ACHOrigDFIName = _sks_eft_setup.ACHOrigDFIName;
            sks_eft_bankUpdate.ACHFileIDModifier = _sks_eft_setup.ACHFileIDModifier;
        }

        if(sks_eft_bankUpdate.FileFromSetup)
        {
            sks_eft_bankUpdate.ACHImmDest = _sks_eft_setup.ACHImmDest;
            sks_eft_bankUpdate.ACHImmDestName = _sks_eft_setup.ACHImmDestName;
            sks_eft_bankUpdate.ACHImmOrig = _sks_eft_setup.ACHImmOrig;
            sks_eft_bankUpdate.ACHImmOrigName = _sks_eft_setup.ACHImmOrigName;

            if(_sks_eft_setup.FillBlock && sks_eft_FileFormat.BlockFill)
            {
                sks_eft_bankUpdate.FillBlock = NoYes::Yes;
            }
            else
            {
                sks_eft_bankUpdate.FillBlock = NoYes::No;
            }

            if(_sks_eft_setup.FileCrLf && sks_eft_FileFormat.CrLf)
            {
                sks_eft_bankUpdate.FileCrLf = NoYes::Yes;
            }
            else
            {
                sks_eft_bankUpdate.FileCrLf = NoYes::No;
            }
        }
        else
        {
            if(!sks_eft_FileFormat.BlockFill)
            {
                sks_eft_bankUpdate.FillBlock = NoYes::No;
            }

            if(!sks_eft_FileFormat.CrLf)
            {
                sks_eft_bankUpdate.FileCrLf = NoYes::no;
            }
        }

        if(sks_eft_bankUpdate.PostedPaymSetupDefaults)
        {
            sks_eft_bankUpdate.AllowPostedPmtReversal = _sks_eft_setup.AllowPostedPmtJournal;
            sks_eft_bankUpdate.RevJournalName = _sks_eft_setup.RevJournalName;
            sks_eft_bankUpdate.RevPaymMode = _sks_eft_setup.RevPaymMode;
            sks_eft_bankUpdate.AutoPostPaymentJournal = _sks_eft_setup.AutoPostJournal;

        }

        sks_eft_bankUpdate.update();

        lastpaymMode = sks_eft_bankUpdate.PaymMode;
    }
}

/// <summary>
/// Validating the inbound file path for AP.
/// </summary>
public boolean SKS_DD_FileLocationValide(SKS_EFT_Bank _SKS_EFT_Bank)
{
    boolean ret = true;

    //<SKS-begin>
    SKS_EFT_InboundFileGroups   sks_EFT_InboundFileGroupsloc;
    SKS_EFT_InboundFileFormat   sks_EFT_InboundFileFormatloc;
    SKS_EB_CommProcess          sks_EB_CommProcessloc;
    SKS_EFT_InboundFileGroupId  sks_EFT_InboundFileGroupIdloc;
    SKS_EFTAR_Bank              sks_EFTAR_Bankloc;
    SKS_EFTBTB_Bank             sks_EFTBTB_Bankloc;
    SKS_EFTCNP_Bank             sks_EFTCNP_Bankloc;
    //</SKS-end>

    SKS_EB_FileStorage  sks_EB_FileStorage;

    //BankFabric
    select firstonly StorageOption_APEFTFXDD from sks_EB_FileStorage;

    //<SKS-begin>
    if ((_SKS_EFT_Bank.InboundFileLoc != '' && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue) ||
         (_SKS_EFT_Bank.APBankFabricInboundFileLocation != '' &&  sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue))
    {
        sks_EFT_InboundFileGroupIdloc = SKS_EFT_FileFormat::find(VendPaymModeTable::find(_SKS_EFT_Bank.PaymMode).SKS_EFT_FormatId).InboundFileGroupId;

        select RecId from sks_EFT_InboundFileGroupsloc
            where sks_EFT_InboundFileGroupsloc.InboundFileGroupId  == sks_EFT_InboundFileGroupIdloc
        join FormatId,SKS_DD_Modules from sks_EFT_InboundFileFormatloc
            where sks_EFT_InboundFileFormatloc.FormatId == sks_EFT_InboundFileGroupsloc.InboundFormatId1;

        select Module from sks_EB_CommProcessloc
            where sks_EB_CommProcessloc.CommID == _SKS_EFT_Bank.InboundCommID;

        switch  (sks_EFT_InboundFileFormatloc.SKS_DD_Modules)
        {
            case SKS_DD_Modules::APEFT:
                /* if (sks_EB_CommProcessloc.Module != SKS_EB_Module::APEFT)
                {
                error("Invalid path");
                ret = false;
                }*/
                break;
            case SKS_DD_Modules::Directdebit:
                /* if (sks_EB_CommProcessloc.Module != SKS_EB_Module::AREFT)
                {
                select firstonly sks_EFTAR_Bankloc
                where sks_EFTAR_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                && sks_EFTAR_Bankloc.SKS_DD_InboundFileLoc == _SKS_EFT_Bank.InboundFileLoc;

                if (sks_EFTAR_Bankloc)
                {
                error(strFmt("Invalid path. This folder already used for AREFT+BankAccount  %3. The file format %1 is for %2 module", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTAR_Bankloc.PaymMode));
                ret = false;
                }
                }*/
                break;
            case SKS_DD_Modules::APEFT_DDSeparatePath:
                if (sks_EB_CommProcessloc.Module == SKS_EB_Module::AREFT)
                {
                    /*select firstonly sks_EFT_Bankloc
                    where sks_EFT_Bankloc.BankAccountId == _SKS_EFTAR_Bank.BankAccountId
                    && sks_EFT_Bankloc.InboundFileLoc == _SKS_EFTAR_Bank.SKS_DD_InboundFileLoc;
                    if (sks_EFT_Bankloc)
                    {
                    ret = checkFailed(strFmt("Invalid path. This folder already used for APEFT+BankAccount %3. The file format %1 is for %2 module", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFT_Bankloc.PaymMode));
                    }*/
                }
                else
                {
                    if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                    {
                        select firstonly sks_EFTAR_Bankloc
                        where sks_EFTAR_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                        && sks_EFTAR_Bankloc.ARBankFabricInboundFileLoc == _SKS_EFT_Bank.APBankFabricInboundFileLocation;
                    }
                    else
                    {
                        select firstonly sks_EFTAR_Bankloc
                        where sks_EFTAR_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                        && sks_EFTAR_Bankloc.SKS_DD_InboundFileLoc == _SKS_EFT_Bank.InboundFileLoc;
                    }

                    if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                    {
                        select firstonly sks_EFTBTB_Bankloc
                        where sks_EFTBTB_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                        && sks_EFTBTB_Bankloc.BTBBankFabricInboundFileLocation == _SKS_EFT_Bank.APBankFabricInboundFileLocation;
                    }
                    else
                    {
                        select firstonly sks_EFTBTB_Bankloc
                        where sks_EFTBTB_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                        && sks_EFTBTB_Bankloc.BTBInboundFileLoc == _SKS_EFT_Bank.InboundFileLoc;
                    }

                    if(sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
                    {
                        select firstonly sks_EFTCNP_Bankloc
                        where sks_EFTCNP_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                        && sks_EFTCNP_Bankloc.CNPBankFabricInboundFileLocation == _SKS_EFT_Bank.APBankFabricInboundFileLocation;
                    }
                    else
                    {
                        select firstonly sks_EFTCNP_Bankloc
                        where sks_EFTCNP_Bankloc.BankAccountId == _SKS_EFT_Bank.BankAccountId
                        && sks_EFTCNP_Bankloc.CNPInboundFileLoc == _SKS_EFT_Bank.InboundFileLoc;
                    }


                    if (sks_EFTAR_Bankloc)
                    {
                        ret = checkFailed(strFmt("@SKS6137", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTAR_Bankloc.PaymMode));
                    }
                    if (sks_EFTBTB_Bankloc)
                    {
                        ret = checkFailed(strFmt("@SKS:SKGBTBInvalidPath", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTBTB_Bankloc.PaymMode));
                    }
                    if (sks_EFTCNP_Bankloc)
                    {
                        ret = checkFailed(strFmt("@SKS:SKGCNPInvalidPath", sks_EFT_InboundFileFormatloc.FormatId,sks_EFT_InboundFileFormatloc.SKS_DD_Modules, sks_EFTCNP_Bankloc.PaymMode));
                    }
                }
                break;
            case SKS_DD_Modules::APEFT_DDSharedPath:
                if (sks_EB_CommProcessloc.Module != SKS_EB_Module::APEFT && sks_EB_CommProcessloc.Module != SKS_EB_Module::AREFT)
                {
                    ret = checkFailed(strFmt("@SKS6139"));
                }
                break;
            default :
                break;
        }
    }
    //</SKS-end>

    return ret;

}

// SKS_FDD_10590 -->
public static SKS_EFT_Bank findByCompany(CompanyId _company, BankAccountId _bankAccountId, PaymMode _paymMode, boolean _forUpdate = false)
{
    SKS_EFT_Bank sks_eft_bank;

    if (_company)
    {
        changecompany(_company)
        {
            sks_eft_bank = SKS_EFT_Bank::find(_bankAccountId, _paymMode, _forUpdate);
        }
    }

    return sks_eft_bank;
}

// SKS_FDD_10590 <--

/// <summary>
/// Inserting the bank code lookups
/// </summary>
public void insert()
{
    VendPaymModeTable       vendPaymModeTable;
    SKS_EFT_FileFormat      lSKS_EFT_FileFormat;
    ;

    ttsbegin;
    if(this.PaymMode != '')
    {
        vendPaymModeTable = VendPaymModeTable::find(this.PaymMode);
    }

    super();

    select firstOnly RecId from lSKS_EFT_FileFormat
        where lSKS_EFT_FileFormat.FormatId == vendPaymModeTable.SKS_EFT_FormatId;

    this.BankAccountLookupAddition(lSKS_EFT_FileFormat);

    ttscommit;
}

/// <summary>
/// Inserting the bank code lookups
/// </summary>
private void BankAccountLookupAddition(SKS_EFT_FileFormat SKS_EFT_FileFormat)
{
    SKS_EFT_BankAccountPaymLookup       lSKS_EFT_BankAccountPaymLookup;
    SKS_EFT_FileFormatFields            SKS_EFT_FileFormatFields;
    RecordInsertList                    ril;

    ril = new RecordInsertList(lSKS_EFT_BankAccountPaymLookup.TableId);

    while select RecId, fixedvalue, defaultcodelookupvaluesrecid from SKS_EFT_FileFormatFields
        where SKS_EFT_FileFormatFields.fileformatrecid == SKS_EFT_FileFormat.recid
    {
        lSKS_EFT_BankAccountPaymLookup.CodeLookupValuesRecId = SKS_EFT_FileFormatFields.DefaultCodeLookupValuesRecId;
        lSKS_EFT_BankAccountPaymLookup.FileFormatFieldsRecId = SKS_EFT_FileFormatFields.RecId;
        lSKS_EFT_BankAccountPaymLookup.BankPaymRecId = this.RecId;
        lSKS_EFT_BankAccountPaymLookup.FixedValue = SKS_EFT_FileFormatFields.FixedValue;
        ril.add(lSKS_EFT_BankAccountPaymLookup);
    }
    ril.insertDatabase();
}
