display container DefaultVendor(SKS_EFT_VendorPaym _sks_eft_vendorpaym)
{
    VendTable   vendtable;
    ImageReference image = ImageReference::constructForSymbol(''); //no symbol
    container imageContainer;
    ;

    if(_sks_eft_vendorpaym.PaymMode && _sks_eft_vendorpaym.PaymSpec && _sks_eft_vendorpaym.VendBankAccountId)
    {

        changecompany(_sks_eft_vendorpaym.VendCompany)
        {
            select * from vendtable where vendtable.PaymMode == _sks_eft_vendorpaym.PaymMode
                && vendtable.PaymSpec == _sks_eft_vendorpaym.PaymSpec
                && vendtable.BankAccount == _sks_eft_vendorpaym.VendBankAccountId
                && vendtable.AccountNum == _sks_eft_vendorpaym.VendAccount;

            if(vendtable)
            {
                image = ImageReference::constructForSymbol(ImageReferenceSymbol::Checkmark, "@SKS1488");
            }
        }

    }

    imageContainer = image.pack();

    return imageContainer;
}

public void insert()
{
    VendPaymModeTable vendPaymModeTable;
    SKS_EFT_FileFormatPaymSpec          SKS_EFT_FileFormatPaymSpec;
    ;

    ttsbegin;
    if(this.PaymMode != '')
    {
        vendPaymModeTable = VendPaymModeTable::find(this.PaymMode);
        this.FormatId = vendPaymModeTable.SKS_EFT_FormatId;
    }

    super();

    select firstOnly RecId from SKS_EFT_FileFormatPaymSpec
        where SKS_EFT_FileFormatPaymSpec.FormatId == this.FormatId &&
              SKS_EFT_FileFormatPaymSpec.PaymSpec == this.PaymSpec;

    this.VendPaymLookupAddition(SKS_EFT_FileFormatPaymSpec, vendPaymModeTable);

    ttscommit;
}

/// <summary>
/// Vend paym lookup addition
/// </summary>
/// <param name = "SKS_EFT_FileFormatPaymSpec">File format paym spec</param>
/// <param name = "lVendPaymModetable">Vend paym mode table</param>
[SuppressBPWarning('BPParameterNotUsed', 'Parameter lVendPaymModetable required for backward support')]
private void VendPaymLookupAddition(SKS_EFT_FileFormatPaymSpec SKS_EFT_FileFormatPaymSpec, VendPaymModeTable lVendPaymModetable)
{
    SKS_EFT_VendorPaymLookup            lSKS_EFT_VendorPaymLookup;
    SKS_EFT_FileFormatPaymSpecFields    SKS_EFT_FileFormatPaymSpecFields;
    RecordInsertList                    ril;

    ril = new RecordInsertList(lSKS_EFT_VendorPaymLookup.TableId);

    while select RecId, fixedvalue, defaultcodelookupvaluesrecid from SKS_EFT_FileFormatPaymSpecFields
        where SKS_EFT_FileFormatPaymSpecFields.fileformatpaymspecrecid == SKS_EFT_FileFormatPaymSpec.recid
    {
        lSKS_EFT_VendorPaymLookup.CodeLookupValuesRecId = SKS_EFT_FileFormatPaymSpecFields.DefaultCodeLookupValuesRecId;
        lSKS_EFT_VendorPaymLookup.FileFormatPaymSpecFieldsRecId = SKS_EFT_FileFormatPaymSpecFields.RecId;
        lSKS_EFT_VendorPaymLookup.VendorPaymRecId = this.RecId;
        lSKS_EFT_VendorPaymLookup.FixedValue = SKS_EFT_FileFormatPaymSpecFields.FixedValue;
        ril.add(lSKS_EFT_VendorPaymLookup);
    }
    ril.insertDatabase();
}

public static SKS_EFT_VendorPaym find(selectableDataArea _vendCompany,
                                      VendAccount _vendAccount,
                                      PaymMode _paymMode,
                                      PaymSpec _paymSpec,
                                      VendBankAccountId _vendBankAccountId,
                                      boolean  _forUpdate = false)
{
    SKS_EFT_VendorPaym sks_eft_vendorpaym;
    LogisticsPostalAddress logisticsPostalAddress;
    Array vendAddrArray = new Array(Types::String);

    if (_vendCompany && _vendAccount && _paymMode && _paymSpec && _vendBankAccountId)
    {
        if (_forUpdate)
        {
            sks_eft_vendorpaym.selectForUpdate(_forUpdate);
        }

        select sks_eft_vendorpaym
            where sks_eft_vendorpaym.VendCompany == _vendCompany &&
                  sks_eft_vendorpaym.VendAccount == _vendAccount &&
                  sks_eft_vendorpaym.PaymMode == _paymMode &&
                  sks_eft_vendorpaym.PaymSpec == _paymSpec &&
                  sks_eft_vendorpaym.VendBankAccountId == _vendBankAccountId;
    }
    if (sks_eft_vendorpaym.WBeneUseVendAddr && sks_eft_vendorpaym.WBeneRemittanceLocation)
    {

        logisticsPostalAddress = LogisticsLocationEntity::location2PostalAddress(sks_eft_vendorpaym.WBeneRemittanceLocation, DateTimeUtil::getSystemDateTime(), true);
        if (logisticsPostalAddress)
        {
            vendAddrArray = SKS_PP_50000Bas::StreetToAddressLineArray(logisticsPostalAddress.Street,2);
            if (vendAddrArray.exists(1))
            {
                sks_eft_vendorpaym.WBeneAddr = vendAddrArray.value(1);
                if (vendAddrArray.exists(2))
                {
                    sks_eft_vendorpaym.WBeneAddr2 = vendAddrArray.value(2);
                }
            }
            sks_eft_vendorpaym.WBeneCity = logisticsPostalAddress.City;
            sks_eft_vendorpaym.WBeneState = logisticsPostalAddress.State;
            sks_eft_vendorpaym.WBeneZipPostal = logisticsPostalAddress.ZipCode;
            sks_eft_vendorpaym.WBeneCountryRegionId = logisticsPostalAddress.CountryRegionId;
        }
    }
    return sks_eft_vendorpaym;
}

static companyBankAccountId getBankAccountId(selectableDataArea _vendCompany,
                                      VendAccount _vendAccount,
                                      PaymMode _paymMode,
                                      PaymSpec _paymSpec,
                                      VendBankAccountId _vendBankAccountId)
{
    SKS_EFT_VendorPaym sks_eft_VendorPaym;
    ;
    Select BankAccountId
        from sks_eft_VendorPaym
        where sks_eft_VendorPaym.VendCompany == _vendCompany &&
            sks_eft_VendorPaym.VendAccount == _vendAccount &&
            sks_eft_VendorPaym.PaymMode == _paymMode &&
            sks_eft_VendorPaym.PaymSpec == _paymSpec &&
            sks_eft_VendorPaym.VendBankAccountId == _vendBankAccountId;

    return sks_eft_VendorPaym.BankAccountId;
}

/// <summary>
/// finds the record across company
/// </summary>
/// <param name = "_company"></param>
/// <param name = "_vendCompany"></param>
/// <param name = "_vendAccount"></param>
/// <param name = "_paymMode"></param>
/// <param name = "_paymSpec"></param>
/// <param name = "_vendBankAccountId"></param>
/// <param name = "_forUpdate"></param>
/// <returns></returns>
public static SKS_EFT_VendorPaym findByCompany(dataAreaId         _company,
                                               selectableDataArea _vendCompany,
                                               VendAccount        _vendAccount,
                                               PaymMode           _paymMode,
                                               PaymSpec           _paymSpec,
                                               VendBankAccountId  _vendBankAccountId,
                                               boolean            _forUpdate = false)
{
    SKS_EFT_VendorPaym sks_eft_vendorpaym;

    if(_company)
    {
        changeCompany (_company)
        {
            sks_eft_vendorpaym = SKS_EFT_VendorPaym::find(_vendCompany, _vendAccount, _paymMode, _paymSpec,_vendBankAccountId, _forUpdate);
        }
    }

    return sks_eft_vendorpaym;
}

//<Issue-20009: Updating SKS_EFT_VendorPaym address by VendBankAccount>
public static void update_EFT_VendorPaymAddress(VendBankAccount _vendBankAccount)
{
    SKS_EFT_VendorPaym sks_EFT_VendorPaym;

    while select forUpdate crosscompany sks_EFT_VendorPaym
        where sks_EFT_VendorPaym.VendAccount == _vendBankAccount.VendAccount
            && sks_EFT_VendorPaym.VendBankAccountId == _vendBankAccount.AccountID
            && sks_EFT_VendorPaym.WBeneUseVendBankAcc
    {
        changecompany(sks_EFT_VendorPaym.DataAreaId) //Issue20483
        {
            SKS_EFT_VendorLoad::UpdateVendorBankAddresses(sks_EFT_VendorPaym);

            ttsbegin;
            sks_EFT_VendorPaym.update();
            ttscommit;
        }
    }
}
