Method.Source:
// BP Deviation Documented
display real calcPaymentRemaining(SKS_LB_JournalAR t)
{
    return t.PmtAmountCur - t.ActualSettleAmountCur;
}

[SysClientCacheDataMethodAttribute]
display container fullSettlement(SKS_LB_JournalAR _sks_LB_JournalAR)
{
    ImageReference image;
    container imageContainer;
    real tolerance;
    str companyid;
    CustParameters custparameters;
    SKS_LB_JournalARApplic lSKS_LB_JournalARApplic;
    real unsettledAmt;
    ;

    tolerance = 0;

    companyid = curExt(); //get current company
    custparameters = CustParameters::findByCompany(companyid); //get custparameters by company

    if(custparameters.RecId != 0)
    {
        tolerance = custparameters.MaxMSTOverUnder;
    }
    unsettledAmt = _sks_LB_JournalAR.PmtAmountCur - _sks_LB_JournalAR.ActualSettleAmountCur;
    select firstOnly RecId
    from lSKS_LB_JournalARApplic
    where lSKS_LB_JournalARApplic.SKS_LB_JournalARRecId == _sks_LB_JournalAR.RecId
        && lSKS_LB_JournalARApplic.FullSettlement == NoYes::No
        && lSKS_LB_JournalARApplic.SettleAmountCur != 0;

    if(lSKS_LB_JournalARApplic.RecId == 0
        && _sks_LB_JournalAR.ActualSettleAmountCur != 0)
    {
        if(unsettledAmt != 0
            && abs(unsettledAmt) <= abs(tolerance))
        {
            image = ImageReference::constructForSymbol(ImageReferenceSymbol::Info, "@SKS569"); // settled fully with tolerance
        }
        else
        {
            image = ImageReference::constructForSymbol(ImageReferenceSymbol::FieldCorrect, "@SYS75399"); // Full settlement
        }
    }
    else if(lSKS_LB_JournalARApplic.RecId == 0 && _sks_LB_JournalAR.ActualSettleAmountCur == 0)
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::NotAvailable, "@SKS568"); // no invoice  to settle
    }
    else
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::Triangle, "@SKS570"); // some combination of partially settled and unsettled invoices
    }

    imageContainer = image.pack();

    return imageContainer;

}

public void insert()
{
    if(this.MOP == '')
    {
        this.MOP = SKS_LB_Bank::find(this.BankAcct, this.FormatID).MethodOfPayment;
    }
    this.DiscApplyAmtCalc();
    super();
}

Public boolean isParentPayment()
{
    SKS_LB_JournalAR l_JournalAR;

    if (this.LBSettlementMethod == SKS_LB_SettlementMethod::External)
    {
        // check if any payments were spawned from this payment
        select count(PmtAmountCur)
            from l_JournalAR
            where l_JournalAR.JournalId == this.JournalId &&
                    l_JournalAR.BatchNum == this.BatchNum &&
                    l_JournalAR.ParentJournalARID == this.RecId;
        if (l_JournalAR.PmtAmountCur == 0)
        {
            return false;
        }
        else
        {
            return true;
        }
    }
    else
    // only external settlement types can have payments spawn additional payments
    {
        return false;
    }
}

public void modifiedField(FieldId _fieldId)
{
    LedgerJournalTxt    ledgerJournalTxt;

    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(SKS_LB_JournalAR, Description):
            ledgerJournalTxt = LedgerJournalTxt::findTxt(this.Description);

            if (ledgerJournalTxt.Txt)
            {
                this.Description = ledgerJournalTxt.Txt;
            }
            break;
    }
}

[SysClientCacheDataMethodAttribute]
display container NotEqual(SKS_LB_JournalAR _sks_LB_JournalAR)
{
    ImageReference image;
    container imageContainer;

    if(_sks_LB_JournalAR.PmtAmountCur != _sks_LB_JournalAR.ActualSettleAmountCur)
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::YellowExclamationPoint);
        imageContainer = image.pack();
        return imageContainer;
    }
    else
    {
        return imageContainer;
    }



}

[SysClientCacheDataMethodAttribute]
display container showDocHandIcon()
{
    ImageReference image;
    container imageContainer;
    DocuRef docuRef;

    if ((select firstonly docuRef where docuRef.RefCompanyId == this.dataAreaId
        && docuRef.RefTableId == this.TableId && docuRef.RefRecId == this.RecId).RecID)
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::Document);
    }
    else
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::PlannedDocument);
    }

    imageContainer = image.pack();

    return imageContainer;
}

[SysClientCacheDataMethodAttribute]
display int statusIcon(SKS_LB_JournalAR _sks_LB_JournalAR)
{
    if(_sks_LB_JournalAR.LedgerJournalTransRecId)
    {
        return 1;
    }
    else
    {
        return -1;
    }

}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    switch(_fieldIdToCheck)
    {
        case fieldNum(SKS_LB_JournalAR, Description):
            return true;
        default:
            ret = super(_fieldIdToCheck);
    }

    return ret;
}

/// <summary>
/// Finds the journal ar record by recid
/// </summary>
/// <param name = "_recId">record id of the journalar record being searched for</param>
/// <returns>SKS_LB_JournalAR record</returns>
static public SKS_LB_JournalAR find(RefRecId _recId)
{
    SKS_LB_JournalAR  journalAR;

    select journalAR where journalAR.RecId == _recId;

    return journalAR;
}

/// <summary>
/// Caclulates the discount amount to apply
/// </summary>
private void DiscApplyAmtCalc()
{
    SKS_LB_JournalARApplic lSKS_LB_JournalARApplic;

    select sum(DiscApplyAmount) from lSKS_LB_JournalARApplic
    where lSKS_LB_JournalARApplic.SKS_LB_JournalARRecId == this.RecId
        && lSKS_LB_JournalARApplic.DiscApplyAmount != 0;

    this.DiscApplyAmt = lSKS_LB_JournalARApplic.DiscApplyAmount;
}

