/// <summary>
/// Deletes the related sks_lb_importing records
/// </summary>
public void delete()
{
    SKS_LB_Importing SKS_LB_Importing;
    delete_from sks_lb_importing where SKS_LB_Importing.ExternalJournalARRecord == this.RecId;
    super();
}
