public display DimensionDisplayValue getBankAccountTable_MainAccount()
{
    //SKS_EFT_InboundFileLine locSKS_EFT_InboundFileLine;
    BankAccountTable locBankAccountTable;
    DimensionAttributeValueCombination locDimensionAttributeValueCombination;


    select firstonly DisplayValue from locDimensionAttributeValueCombination
    join AccountID, LedgerDimension from locBankAccountTable
        where locDimensionAttributeValueCombination.recid == locBankAccountTable.LedgerDimension
        && locBankAccountTable.AccountID == this.BankAccountId;

    return locDimensionAttributeValueCombination.DisplayValue;
}

