    public void modifiedField(FieldId _fieldId)
    {
        SKS_PP_Setup    sks_PP_Setup; //parameters table

        super(_fieldId);

        // To write code in the form to enable /disable these fields accordingly
        switch (_fieldId)
        {
            case fieldNum(SKS_PP_Bank, UseeBankRecCustSettlementSetupDefaults):
                if (this.UseeBankRecCustSettlementSetupDefaults)
                {
                    sks_PP_Setup = SKS_PP_Setup::find();
                    this.MatchingRulesGroupIdCustSettle         = sks_PP_Setup.MatchingRulesGroupIdCustSettle;
                    this.InvoiceSearchCrossComp                 = sks_PP_Setup.InvoiceSearchCrossComp;
                    this.ApplyDiscOfImportFile                  = sks_PP_Setup.ApplyDiscOfImportFile;
                    this.DefaultDimensionFromInvoice            = sks_PP_Setup.DefaultDimensionFromInvoice;
                    this.AutoPostOnImportOptionPayEqualsInvoice = sks_PP_Setup.AutoPostOnImportOptionPayEqualsInvoice;
                    this.AutoPostOnImportOptionInvOverpay       = sks_PP_Setup.AutoPostOnImportOptionInvOverPay;
                    this.AutoPostOnImportOptionInvShortpay      = sks_PP_Setup.AutoPostOnImportOptionInvShortpay;
                    // SKS - 9479 -->
                    this.BRInvoiceNotFound                      = sks_PP_Setup.BRInvoiceNotFound;
                    this.BRAutoPostInvoiceNotFound              = sks_PP_Setup.BRAutoPostInvoiceNotFound;
                    this.BRCustRemitAnalysisRuleGroup           = sks_PP_Setup.BRCustRemitAnalysisRuleGroup;
                    this.BRCustomerSearchAcrossCompany          = sks_PP_Setup.BRCustomerSearchAcrossCompany;
                    // SKS - 9479 <--
                }
                break;
             // SKS_BR5 -->
            case fieldNum(SKS_PP_Bank , BRFromSetup) :
                if (this.BRFromSetup == NoYes::Yes)
                {
                    sKS_PP_Setup = SKS_PP_Setup::find();

                    this.BRCreateBankStatementsIfNoTrans = sKS_PP_Setup.BRCreateBankStatementsIfNoTrans;
                }

            //8663 CR --->
            case fieldNum(SKS_PP_Bank, UseeBankRecBankStatementMonitorDefaults):
                if (this.UseeBankRecBankStatementMonitorDefaults)
                {
                    sks_PP_Setup = SKS_PP_Setup::find();
                    this.BRBankHolidayGroupID = sKS_PP_Setup.BRBankHolidayGroupID;
                    this.BRNotifyStatements   = sKS_PP_Setup.BRNotifyStatements;
                    this.BRNotifyMinusDays    = sKS_PP_Setup.BRNotifyMinusDays;
                    this.BRBatchJobId         = sKS_PP_Setup.BRBatchJobId;
                    this.BREmail              = sKS_PP_Setup.BREmail;
                }
                break;
             //8663 CR <---
            // SKS_BR5 <--
            // IHB 9564 --->
            case fieldNum(SKS_PP_Bank, IHB_UseInterestCalcDefaults):
                if (this.IHB_UseInterestCalcDefaults)
                {
                    sks_PP_Setup = SKS_PP_Setup::find();
                    this.IHB_InterestCalcFreq               = sks_PP_Setup.IHB_InterestCalcFreq;
                    this.IHB_InterestCalcMethod             = sks_PP_Setup.IHB_InterestCalcMethod;
                    this.IHB_InterestDaysCalcMethod         = sks_PP_Setup.IHB_InterestDaysCalcMethod;
                    this.IHB_InterestMarkupId               = sks_PP_Setup.IHB_InterestMarkupId;
                    this.IHB_InterestExpenseLedgerDimension = sks_PP_Setup.IHB_InterestExpenseLedgerDimension;
                    this.IHB_InterestIncomeLedgerDimension  = sks_PP_Setup.IHB_InterestIncomeLedgerDimension;
                    this.IHB_InterestExpenseBankTransType   = sks_PP_Setup.IHB_InterestExpenseBankTransType;
                    this.IHB_InterestIncomeBankTransType    = sks_PP_Setup.IHB_InterestIncomeBankTransType;
                    this.IHB_InterestRateFormat             = sks_PP_Setup.IHB_InterestRateFormat;
                    this.IHB_CommId                         = sks_PP_Setup.IHB_CommId;
                    this.IHB_InterestFilePath               = sks_PP_Setup.IHB_InterestFilePath;
                    this.IHBInterestBankFabricLocation      = sks_PP_Setup.IHBInterestBankFabricLocation;
                    this.IHB_CalculationFrequencyEndMonth   = sks_PP_Setup.IHB_CalculationFrequencyEndMonth;
                    this.IHB_CalculationFrequencyStartMonth = sks_PP_Setup.IHB_CalculationFrequencyStartMonth;
                    this.IHB_InterestRepaymentNonCompounded = sks_PP_Setup.IHB_InterestRepaymentNonCompounded; //FDD-11448
                }
                break;
            // IHB 9564 <---

            //SKS_ElectronicBankAPIChanges
			case fieldNum (SKS_PP_Bank, BRFormatID):
				if (this.BRFormatID != this.orig().BRFormatID)
                {
                    SKS_BR_FileFormat SKS_BR_FileFormat;
                    select firstonly SKS_BR_FileFormat where SKS_BR_FileFormat.FormatID == this.BRFormatID;

                    if (SKS_BR_FileFormat.RecId && SKS_BR_FileFormat.APIFormatType == SKS_EB_APIFormatType::API)
                    {
                        this.LookBackDays = 7;
                        this.FirstStatementDate = systemDateGet();
                    }
                }
                break;
            //SKS_ElectronicBankAPIChanges

            default:
                break;
        }
    }

SKS_PP_Bank SetAcctSub(CompanyBankAccountId _acct)
{
    SKS_PP_Bank SKS_PP_Bank;
    ;

    select SKS_PP_Bank
        WHERE SKS_PP_Bank.BankAccountId == _acct;

    _acct = SKS_PP_Bank.BankAccountId;

    return SKS_PP_Bank;
}

/// <summary>
///    Finds the specified record in the <c>SKS_PP_Bank</c> table.
/// </summary>
/// <param name="_bankAcctId">
///    The id of the <c>SKS_PP_Bank</c> record to find.
/// </param>
/// <param name="_company">
///    The company id of the <c>SKS_PP_Bank</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
///    A record in the <c>SKS_PP_Bank</c> table; otherwise, an empty record.
/// </returns>
public static SKS_PP_Bank find(BankAccountID _bankAcctId, SelectableDataArea _company = curext(),
                                 boolean            _forUpdate = false)
{
    SKS_PP_Bank sks_pp_bank;
    Container company;
    ;

    if (_bankAcctId)
    {
        if (_forUpdate)
        {
            sks_pp_bank.selectForUpdate(_forUpdate);
        }
        company = [_company];
        select crossCompany:company sks_pp_bank
            where sks_pp_bank.BankAccountId == _bankAcctId;
    }

    return sks_pp_bank;
}

