            sks_pp_bankUpdate.UseeBankRecCustSettlementSetupDefaults ||
	          sks_pp_bankUpdate.UseeBankRecBankStatementMonitorDefaults || //20603
	          sks_pp_bankUpdate.IHB_UseInterestCalcDefaults || // 20603
            sks_pp_bankUpdate.CCR_FromSetup == NoYes::Yes //SKS_FDD_13903
        {
