            if(sks_pp_bankUpdate.PPFromSetup)
            {
                sks_pp_bankUpdate.PPOutServerSideFileProc = _sks_pp_setup.PPOutServerSideFileProc;
                sks_pp_bankUpdate.PPDelete = _sks_pp_setup.PPDelete;
