/// <summary>
/// Validation for invoice not found.
/// </summary>
/// <returns>Return true.</returns>
// CR-9479
public boolean validateWrite()
{
    boolean ret;
    SKS_LB_MatchingRules    matchingRules;

    ret = super();

    if(this.BRInvoiceNotFound != SKS_BR_InvoiceNotFound::None && this.BRCustRemitAnalysisRuleGroup == '')
    {
        ret = ret && checkFailed("@SKS:SKGCustRemitRuleGrpMust");
    }
    select firstonly RecId from matchingRules
        where matchingRules.GroupID      == this.MatchingRulesGroupIdCustSettle
            && (matchingRules.lookupType == SKS_LB_MatchType::CustomerAndAmount
            || matchingRules.lookupType  == SKS_LB_MatchType::CustomerAndInvBalance);           
    if (matchingRules.RecId && 
        this.BRCustRemitAnalysisRuleGroup == '' && 
        this.BRInvoiceNotFound == SKS_BR_InvoiceNotFound::None)
    {
        ret = ret && checkfailed("@SKS:SKGCustRemitRuleGrpMust");
    }
    return ret;
}

    /// <summary>
    /// validates the filed for SKS_IHBLinkedinhousebankaccount
    /// </summary>
    /// <param name = "_fieldIdToCheck"></param>
    /// <returns></returns>
    /// IHB_3.3.21 SKS 9536
    public boolean validateField(FieldId _fieldIdToCheck)
    {
        boolean         ret;
        SKS_PP_Bank     sks_PP_Bank;

        ret = super(_fieldIdToCheck);

        if(ret)
        {
            switch(_fieldIdToCheck)
            {
                // User cannot select an In-house bank account that has been already linked to another account
                case fieldNum(SKS_PP_Bank, SKS_IHBLinkedinhousebankaccount):
                    if (this.SKS_IHBLinkedinhousebankaccount)
                    {
                        // Bug 10116 --> 
                        if(this.SKS_IHBLinkedinhousebankcompany)
                        {
                            select firstonly BankAccountId, SKS_IHBLinkedinhousebankaccount from sks_PP_Bank
                                    where sks_PP_Bank.SKS_IHBLinkedinhousebankaccount == this.SKS_IHBLinkedinhousebankaccount &&
                                          sks_PP_Bank.SKS_IHBLinkedinhousebankcompany == this.SKS_IHBLinkedinhousebankcompany;
                        }
                        else
                        {
                            select firstonly BankAccountId, SKS_IHBLinkedinhousebankaccount from sks_PP_Bank
                                    where sks_PP_Bank.SKS_IHBLinkedinhousebankaccount == this.SKS_IHBLinkedinhousebankaccount;
                        }
                        // Bug 10116 <--

                        if(sks_PP_Bank)
                        {
                            ret = checkFailed(strFmt("@SKS:SKGInfoBankAc", this.SKS_IHBLinkedinhousebankaccount, sks_PP_Bank.BankAccountId));
                        }
                        else
                        {
                            ret = true;
                        }
                    }
                    break;

                // User cannot select current company
                case fieldNum(SKS_PP_Bank, SKS_IHBLinkedinhousebankcompany):
                    if (this.SKS_IHBLinkedinhousebankcompany == curExt())
                    {
                        ret = checkFailed("@SKS:SKGDiffCom");
                    }
                    break;

                //SKS_ElectronicBankAPIChanges
                case fieldNum (SKS_PP_Bank, LookBackDays):
                    if (this.LookBackDays < 0 || this.LookBackDays > 31)
                    {
                        ret = checkFailed('@SKS:NewLabel9');
					}
					break;
                //SKS_ElectronicBankAPIChanges
            }
        }
        return ret;
    }

/// <summary>
/// This method will update last statement date generated
/// </summary>
/// <param name = "_bankAccountId"></param>
/// <param name = "_statementDate"></param>
public static void updateLastStatementDate(
    CompanyBankAccountId    _bankAccountId,
    TransDate               _statementDate)
{
    SKS_PP_Bank inhouseBankAccountSetup;

    select forupdate firstonly
        inhouseBankAccountSetup
        where
        inhouseBankAccountSetup.BankAccountId == _BankAccountId;

    if(inhouseBankAccountSetup.RecId > 0)
    {
        ttsbegin;
        inhouseBankAccountSetup.IHB_LastStatementDate = _statementDate;
        inhouseBankAccountSetup.update();
        ttscommit;
    }

}

/// <summary>
/// Find the IHB Bank and Linked in-house bank.
/// </summary>
/// <param name = "_bankAccountId">The id of the <c>SKS_PP_Bank</c> record to find.</param>
/// <param name = "_company">The company id of the <c>SKS_PP_Bank</c> record to find.</param>
/// <returns>Return <c>SKS_PP_Bank</c> buffer.</returns>
public static SKS_PP_Bank findIHBBank(BankAccountID _bankAccountId, SelectableDataArea _company = curext())
{
    SKS_PP_Bank sks_pp_bank;

    if(_bankAccountId)
    {
        changecompany(_company)
        {
            select sks_pp_bank
                where sks_pp_bank.BankAccountId == _bankAccountId &&
                    sks_pp_bank.SKS_IHBInhouseBank == NoYes::Yes &&
                    sks_pp_bank.SKS_IHBLinkedinhousebankcompany &&
                    sks_pp_bank.SKS_IHBLinkedinhousebankaccount;
        }
    }
    return sks_pp_bank;
}

/// <summary>
/// SKS bug 10171
/// </summary>
public static server void UpdateFromSetup_EFT(SKS_EFT_Setup _sks_eft_setup)
{
    SKS_PP_Bank sks_pp_bank;

    update_recordset sks_PP_Bank
            setting APEMAttachmentName = _sks_eft_setup.APEMAttachmentName,
                    APEMReplyToAddr = _sks_eft_setup.APEMReplyToAddr,
                    APEMReplytoName = _sks_eft_setup.APEMReplytoName,
                    APEMCompanyName = _sks_eft_setup.APEMCompanyName,
                    APEMTemplateAttach = _sks_eft_setup.APEMTemplateAttach,
                    APEMTemplateNoAttach = _sks_eft_setup.APEMTemplateNoAttach,
                    APEMTemplateLanguage = _sks_eft_setup.APEMTemplateLanguage
            where sks_PP_Bank.SKS_IHBInhouseBank == NoYes::Yes &&
                  sks_PP_Bank.APEMFromSetup == NoYes::Yes;

    update_recordset sks_PP_Bank
            setting APContactName = _sks_eft_setup.APContactName,
                    APContactPhone = _sks_eft_setup.APContactPhone,
                    APContactEmail = _sks_eft_setup.APContactEmail
            where sks_PP_Bank.SKS_IHBInhouseBank == NoYes::Yes &&
                  sks_PP_Bank.AP_EMFromSetup == NoYes::Yes;

}
